body.script:
  $(document).ready(function() {
    $('.search-results').hide();

    $("#q").on("keyup", function(e){

      if($.trim(this.value).length) {
        $(".search-results").fadeIn("fast");
      }else{
        $(".search-results").fadeOut("fast");
      }
    });

    var pageTemplate = '
' +
      '
' +
        '
'+
        '{{#page_link}}{{/page_link}}'+
        '{{^page_link}}{{/page_link}}'+
        '{{{_highlightResult.name.value}}}'+
        '
{{{_highlightResult.content_summary.value}}}'+
      ''+
    '';

    var livefeedTemplate = '
' +
      '
' +
        '
{{{_highlightResult.author_name.value}}}'+
        '
{{{_highlightResult.status.value}}}'+
      ''+
    '';

    var articleTemplate = '
' +
      '
' +
        ''+
      '' +
      '
' +
        '
{{{_highlightResult.title.value}}}'+
        '
{{{_highlightResult.content_summary.value}}}'+
      ''+
    '';

    var eventTemplate = '
' +
      '
' +
        '
{{{_highlightResult.title.value}}}'+
        '
{{{_highlightResult.when.value}}}'+
        '
{{{_highlightResult.where.value}}}'+
      ''+
    '';

    var scoreTemplate = '
' +
      '
' +
        '
{{{_highlightResult.away_team.value}}}{{{away_score}}}    {{{home_score}}}{{{_highlightResult.home_team.value}}}'+
        '
{{{_highlightResult.when.value}}}'+
        '
{{{_highlightResult.where.value}}}'+
        '
{{{_highlightResult.description.value}}}'+
      ''+
    '';

    var staffTemplate = '
' +
      '
' +
        ''+
      '' +
      '
' +
        '
{{{_highlightResult.full_name.value}}}'+
        '
'+
          ''+
            '{{{_highlightResult.title.value}}}'+
            '
'+
            '{{{_highlightResult.department.value}}}'+
            '
'+
            '{{{phone_number}}}'+
            '
'+
            '{{{email}}}'+
          ''+
        ''+
      ''+
    '';

    var documentTemplate = '
' +
      '
' +
        '
'+
          '{{{_highlightResult.name.value}}} (DOC)'+
        ''+
        '
'+
