function rotatorImageSetup(wrapperClass, sliderId){
	var screenWidth = window.innerWidth;
		var slider = $('.' + wrapperClass).find("#" + sliderId);
		if (screenWidth<530){							
			$("#" + $(slider).attr("id") + " li").each(function(){
				var img = $(this).children("img");
				var imgUrl = $(img).attr("data-image-url");
				var imgThumbnail = $(img).attr("data-image-thumb-url");

				$(img).attr("src", imgThumbnail);
			});			
		}
		else
		{
			$("#" + $(slider).attr("id") + " li").each(function(){
				var img = $(this).children("img");
				var imgUrl = $(img).attr("data-image-url");
				var imgThumbnail = $(img).attr("data-image-thumb-url");

				$(img).attr("src", imgUrl);
			});		
		}
}

