	var delay = (function() {
    var timer = 0;
    return function(callback, ms){
        clearTimeout(timer);
        timer = setTimeout(callback, ms);
    }
	})();
		
	onResize = function() {
	// Check size
	if ($(window).width() <= 800){	
		// Small Image
		$('.bullock').css('background-image', 'linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.05))' + ', url("/Portals/_default/skins/gov/images/avalanche-lake-sm.jpg")');
	}
	
	else {
		// Full Size Image
		$('.bullock').css('background-image', 'linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.05))' + ', url("/Portals/_default/skins/gov/images/avalanche-lake.jpg');
	}
	}
	
	$(document).ready(onResize);
	$(document).resize(onResize);
	
	$(window).resize(function() {
    delay(function(){
        onResize();
    },100)
});
