// GA EVENTS
$(document).ready(function() {
    $('.footer_link').off();
    $('.footer_link').on('click', function(event){
        event.preventDefault();
        core.tracker.trackEvent({
            category : 'FooterNavigationEvent',
            action : 'Footer Navigation Click',
            opt_label : $(this).html()
        });
        if($(this).attr('target') === "_blank"){
            window.open($(this).attr('href'),'_blank');return false;
        } else {
            window.location = $(this).attr('href');
        }
    });

    $('.footer_mobile_header').off();
    $('.footer_mobile_header').on('click',function(){
        if ($(this).siblings('.footer-nav').hasClass('in')){
            $(this).removeClass('footer_header_open');
            $(this).siblings('.footer_row_anchor_close').hide();
            $(this).siblings('.footer_row_anchor_open').show();
            setTimeout(function() {
                if($('.list-unstyled').attr('style')) {
                    $('.list-unstyled').removeAttr('style');
                }
            }, 200);
        }else{
            $(this).addClass('footer_header_open');
            $(this).siblings('.footer_row_anchor_close').show();
            $(this).siblings('.footer_row_anchor_open').hide();
        }
    });

    $('.footer_row_anchor').off();
    $('.footer_row_anchor').on('click', function(event){
        event.preventDefault();
        $(this).siblings('.footer_mobile_header').trigger('click');
        if ($(this).hasClass('footer_row_anchor_close')){
            $(this).siblings('.footer_row_anchor_open').focus();
        }else{
            $(this).siblings('.footer_row_anchor_close').focus();
        }
    });

});

	function OI_carousel(params){}
	(function(){
	    var THIS = this;
	    this.defined = function(x){
	        //
	        return typeof x != 'undefined';
	    };

	    this.events = function(params){
	    	// $('.one_item_carousel_pause').off();
			$(params.container).find('.one_item_carousel_pause').on('click', function(){
			// $('.one_item_carousel_pause').on('click', function(){
				// $('.OIC_items_container').slick('slickPause');
				$(params.container).find('.OIC_items_container').slick('slickPause');
				// $('.one_item_carousel_pause').hide();
				$(this).hide();
				$(params.container).find('.one_item_carousel_play').show();
			});
			$('.one_item_carousel_play').on('click', function(){
				$(params.container).find('.OIC_items_container').slick('slickPlay');
				// $('.one_item_carousel_play').hide();
				$(this).hide();
				$(params.container).find('.one_item_carousel_pause').show();
				// $('.one_item_carousel_pause').show();
			});
	    }

	    this.init = function(params){
	    	autoplay = true;
	    	if (params.autoplay !== undefined){
	    		autoplay = params.autoplay;
	    	}
            $(params.container).find('.OIC_items_container').slick({
	            dots: true,
	            infinite: true,
	            speed: 300,
	            autoplay: autoplay,
	            autoplaySpeed: 7000,
	            slidesToShow: 1,
	            slidesToScroll: 1,
	            responsive : true,
	            prevArrow : $(params.container).find('.one_item_carousel_prev'),
	            nextArrow : $(params.container).find('.one_item_carousel_next'),
	            respondTo : params.container
	        });
	        THIS.events(params);
        }

        this.pause = function(params){
        	$(params.container).find('.OIC_items_container').slick('slickPause');
        	$(params.container).find('.one_item_carousel_pause').hide();
        	$(params.container).find('.one_item_carousel_play').show();
        }



	}).apply(OI_carousel);
