$url = 'https://durhammuseum.org/';

function onScroll() {
	if( jQuery(window).scrollTop() >= jQuery('.scroller-inner .header').offset().top) {
		jQuery('.fixed-header').addClass('visible');
		jQuery('.footer').addClass('visible');
	} else {
		jQuery('.fixed-header').removeClass('visible');
		jQuery('.footer').removeClass('visible');

		if(jQuery('.footer').hasClass('open')) {
			jQuery('.footer-widgets').animate({'height' : 0}, 350);
			jQuery('.footer').removeClass('open');
		}
	}

	if(jQuery('body').hasClass('menu-open')) {
		jQuery('#side-menu').css('top', jQuery(window).scrollTop());
	}

	jQuery('.home .main-menu>ul>li>a[href^="' + $url + '#"]').each(function(index) {
		jQuery(this).parents('li').removeClass('current');
		var section = jQuery(this).attr('href').replace($url,'');

		if(jQuery(section).length) {
			if (jQuery(window).scrollTop() >= jQuery(section).offset().top-jQuery('.header').height()) {
			  jQuery('.main-menu li').removeClass('current');
			  jQuery('.main-menu a[href="'+$url+section+'"]').parents('li').addClass('current');
			}
		}
		//if($(window).scrollTop() + $(window).height() == $(document).height()) {
		  //$('.menu a').removeClass('current');
		  //$('.menu a:last').addClass('current');
		//}
	  });
}

onScroll();

jQuery('.home .main-menu a[href^="' + $url + '#"]').click(function (){
	var href = jQuery(this).attr('href').replace($url,'');		
	jQuery('html, body').animate({scrollTop: jQuery(href).offset().top-jQuery('.header').height()}, 800);
	return false;
});
