var Y = PROD_YUI.use(['ixl_util-commonjs_Cookie', 'event-delegate']);


(function(win, doc) {
  function loadScript(src, onComplete) {
    var s = doc.createElement('script');
    s.type = 'text/javascript';
    s.async = true;
    s.src = src;

    if (onComplete) {
      s.onreadystatechange = function() {
        if (this.readyState === 'complete' || this.readyState === 'loaded') {
          onComplete();
        }
      };
      s.onload = onComplete;
    }

    var fs = doc.getElementsByTagName('script')[0];
    fs.parentNode.insertBefore(s, fs);
  }

  function loadPixel(ppcSrc) {
    var image = new Image(1,1);
    image.src = ppcSrc;
  }


  (function() {
    // in old GA, we did tracking with "gaq.push()"
    // in the new GA, tracking is done via a function in form "gaq(TRACKING_PARAMS)"
    // this function is for pre-load before GA JS is loaded on page
    // similar to doing "var gaq = win['gaq'] || []"
    win['gaq'] = win['gaq'] ||
            function() {
                (win['gaq'].q = win['gaq'].q || []).push(arguments);
            };

    gaq('create', 'UA-18268683-1', 'auto');
    gaq('require', 'linkid', 'linkid.js');
    gaq('set', 'anonymizeIp', true);


    gaq('set', 'dimension1', 'NL');
    gaq('send', 'pageview');


    //record all pdf clicks in community pages and send them to google analytics.
    Y.use('event-delegate', function () {
      Y.delegate('mousedown', function(e) {
          var pdfUri = e.currentTarget.getAttribute('href');
          // Some times the filter fails, so we want to double check again.
          // And only send to ga if it is an pdf link.
          if (pdfUri && pdfUri.lastIndexOf(".pdf") === pdfUri.length - 4) {
            gaq('send', 'pageview', pdfUri);
            gaq('send', 'event', 'PDF_Click', pdfUri, win.location.pathname);
          }
      }, 'body', 'a[href$=".pdf"]');
    });

    gaq.l = new Date().getTime();
    win['GoogleAnalyticsObject'] = 'gaq';
    loadScript('//www.google-analytics.com/analytics.js');
  })();

}(window, document));






