      var cookieUtils = require('ixl/util-commonjs/Cookie');
      var expires = new Date();
      expires.setFullYear(expires.getFullYear() + 1);
      cookieUtils.setCookieViaServerDelegate(
        "lastLoginStatus",
        "false",
        {
          path: "/",
          domain: "ixl.com",
          expires: expires,
          secure: true
        }
      );
    });
  })();






  ;(function () {
    try { 
        var pattern = new RegExp("^(\\d{1,3}\\.){4}\\d{16}$");
        var trackingIdCookieName = "TRID";

        Y.use('ixl_util-commonjs_Cookie', function () {
            var cookieUtils = require('ixl/util-commonjs/Cookie');

            if (pattern.test(localStorage.getItem(trackingIdCookieName))) {
              var expires = new Date();
              expires.setFullYear(expires.getFullYear() + 1);
              cookieUtils.setCookieViaServerDelegate(
                trackingIdCookieName,
                localStorage.getItem(trackingIdCookieName),
                {
                  path: "/",
                  domain: "ixl.com",
                  expires: expires,
                  secure: true
                }
              );
            }



            else if (pattern.test(cookieUtils.getCookie(trackingIdCookieName))) {
              localStorage.setItem(trackingIdCookieName, cookieUtils.getCookie(trackingIdCookieName));
            }
        });
    } catch(e) {
        return false;
    }
  })();

})();

