div.script:
    (function ($) {
        var jQuery = $;

        function checkForEnter(evnt, objID) {
            if (evnt.keyCode == 13) {
                var obj = document.getElementById(objID);
                if (obj.onclick) {
                    obj.click();
                }
                else if (obj.href) {
                    location.href = obj.href;
                }
            }
        }
        function getQueryVariable(variable) {
            var query = window.location.search.substring(1);
            var vars = query.split("&");
            for (var i = 0; i < vars.length; i++) {
                var pair = vars[i].split("=");
                if (pair[0] == variable) {
                    return pair[1];
                }
            }
        }
        function alertSubmitted() {
            if (getQueryVariable("mod")) {
                alert("Your comment has been submitted for moderation.");
            }
        }

        $(function () {
            alertSubmitted();

            $(".form--submit .info").hide();

            $("#main_0_0_pnlCommentsForm input[id*='captcha']").keydown(function (event) {
                checkForEnter(event, 'main_0_0_btnSubmit');
            });

            $(".lcms-button.reply").click(function (e) {
                e.preventDefault();
                $("#hdnReplyToCommentID").val($(this).data("blog-comment-id"));
                var frm = $(".post__comments--form");
                $(this).after(frm);
                $(".form--submit .info").show();
            });

            $(".form--submit .info").click(function (e) {
                e.preventDefault();
                $("#hdnReplyToCommentID").val("");
                var frm = $(".post__comments--form");
                $("#commentsForm_placeholder").append(frm);
                $(".form--submit .info").hide();
                frm.find('input[type=text], textarea').val('');
            });
        });

    })(LCMS.jq);

