var windowWidth;
$(document).ready(function (){
        windowWidth= $(window).width();
        $( window ).resize(function() {
            windowWidth = $(window).width();
        });

        $('.sf-menu').superfish({
            onBeforeShow : function (){                 
                if(!this.is('.sf-menu>li>ul')){
                    var subMenuWidth = $(this).width();
                    var parentLi = $(this).parent();                    
                    var parentWidth = parentLi.width();
                    var subMenuRight = parentLi.offset().left + parentWidth + subMenuWidth;
                    if(subMenuRight > windowWidth){
                        $(this).css({'left': 'auto', 'right': parentWidth+'px'});
                    } else {
                        $(this).css({'left': '', 'right': ''});
                    }
                }
            }
        });
 });    
    
    
    
$(function(){
    $('ul.sf-menu').supersubs({
        minWidth:    12,
        maxWidth:    27,
        extraWidth:  1
    }).superfish({
        autoArrows:    true,
        dropShadows:   true,
        hoverClass:    'sfHover',
        pathClass:  'current',             
        delay:       0,
        animation:   {opacity:'show',height:'show'},
        speed:       'fast'
    });
    $('ul.sf-menu > li > a span').remove();
});

br.script:
jQuery.colorbox.settings.maxWidth  = '100%';
jQuery.colorbox.settings.maxHeight = '100%';

jQuery(window).resize(function(){
    resizeColorBox();
    window.addEventListener("orientationchange", resizeColorBox, false);
});

var resizeTimer;
function resizeColorBox() {
    if (resizeTimer) {
        clearTimeout(resizeTimer);
    }
    resizeTimer = setTimeout(function() {
        if (jQuery('#cboxOverlay').is(':visible')) {
            jQuery.colorbox.reload();
        }
    }, 300);
}    
$(document).ready(function(){
    $("a[rel='galImg']").colorbox({});
    $("#click").click(function(){
            $('#click').css({"background-color":"#f00", "color":"#fff", "cursor":"inherit"}).text("Open");
            return false;
    });
    $("img.lazy").lazyload({threshold:200});
});

