$(document).ready(function() {
    $('#extra-nav .menu-btn > span').keydown(function() {
        if (event.keyCode == 32) {
            event.preventDefault();
        }
    });
    $(document).keydown(function(e) {
        if (e.keyCode == 27) {

            $("#extra-nav .menu-btn, #extra-nav .menu-btn > span").removeClass('active');

        }
    });
    $('#extra-nav .menu-btn > span').attr('aria-expanded', 'false');

    $('html, .menu-close').click(function() {
        $("#extra-nav .menu-btn, #extra-nav .menu-btn > span").removeClass('active');
    });
    $('.menu-close').keyup(function() {
        if (event.keyCode == 13 || event.keyCode == 32) {
            $("#extra-nav .menu-btn, #extra-nav .menu-btn > span").removeClass('active');
        }
    });

    function extranav2() {
        $("#left-nav").removeClass('active');
         $("#right-nav .menu-btn > span").removeClass('active');
        $("#right-nav .menu-btn").removeClass('active');
        $("#extra-nav .menu-btn > span").toggleClass('active');
        $("#extra-nav .menu-btn").toggleClass('active');
        $("#change_language_form form").removeClass('focus');
        $("#change_language_form .styledSelect").removeClass('active');
        $("#change_language_form .options").removeClass('open');
        $('div.styledSelect').attr('aria-expanded', 'false');
        if ($('#extra-nav .menu-btn > span').hasClass('active')) {
            $('#header').addClass('active');
        } else {
            $('#header').removeClass('active');
        }
        if ($("#extra-nav .menu-btn > span").attr('aria-expanded') === 'true') {
            $("#extra-nav .menu-btn > span").attr('aria-expanded', 'false');
        } else {
            $("#extra-nav .menu-btn > span").attr('aria-expanded', 'true');
        }
        $("#header #main-nav").removeClass('menu-opened');
        $('li.has-child').removeClass('open');
        $('li').removeClass("hide-item");
    }
    $("#extra-nav .menu-btn > span").keyup(function() {
        if (event.keyCode == 13 || event.keyCode == 32) {
            extranav2(this);
        }
    }).click(function() {
        extranav2(this);
    });

    function closeextranav() {
        $("#extra-nav .menu-btn, #extra-nav .menu-btn > span").removeClass('active');

    }
    $("#left-nav .menu-btn > span, #right-nav .menu-btn > span, #header #change_language_form .select").keyup(function() {
        if (event.keyCode == 13 || event.keyCode == 32) {
            closeextranav(this);
        }
    }).click(function() {
        closeextranav(this);
    });

});
