				"itemizedBullets"	: "on",
				"thumbnails"		: "off",
				"thumbnailWidth"	: 50,
				"thumbnailHeight"	: 32,
				"thumbnailControls"	: "off",
				"thumbnailLimit"	: 3,
				"addClass"		: "unclassed",
				"moreLinkText"		: "off" 
			});
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("#hp-content div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("#hp-content div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500);
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
		//DOC .sw-mystart-button.home -- ELEMENT districtButton SHOWS OR HIDES THE DISTRICT HOME BUTTON
		var districtButton = 'Yes';
		if(districtButton == "no"){
			$(".sw-mystart-button.home").hide();
		}
        //CHANGE SIGN IN URL
        $(".sw-mystart-button.signin > a").attr({
        	"href": "https://www.washoeschools.net/site/SAMLLogin.aspx?IgnoreRedirect=true",
        	"target": "_blank"
        });
		//MOVE MYSTART BUTTONS INTO HEADER
		$(".sw-mystart-dropdown").prependTo("#gb-mystart");
		$(".sw-mystart-button, #sw-mystart-account").prependTo("#gb-mystart");
		$(".sw-mystart-button.home").appendTo("#gb-mystart");
		$("#sw-mystart-search").appendTo("#gb-icons");
		//CHANGE REGISTER LINK TEXT
		$(".sw-mystart-button.register span").text("Create Account");
		//CHANGE SCHOOLLIST DROPDOWN SELECTOR TEXT
        $(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("FIND A SCHOOL");


		// ********** HEADER ********** //
		// ELEMENT schoolnameToggle SHOWS OR HIDES THE SCHOOL NAME
		var sitenameToggle = 'false'; 
		if(sitenameToggle == "true"){
			$("#gb-sitename").prepend('
Washoe County School District');
		}
		// ELEMENT taglineToggle SHOWS OR HIDES THE SCHOOL NAME
		var sitenameToggle = 'false'; 
		if(sitenameToggle == "true"){
			$("#gb-sitename").append('
');
		}
		// #gb-logo -- ELEMENT logoToggle SHOWS OR HIDES THE SCHOOL LOGO
		var logoToggle = 'true'; 
		if(logoToggle == "true"){
			$("#gb-logo").html('');
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	// #sp-breadcrumbs -- ELEMENT breadcrumbs SHOWS OR HIDES THE BREADCRUMBS
		var breadcrumbs = 'Yes';
		if(breadcrumbs == "no"){
			$("#sp-breadcrumbs").hide();
		}
        	//MOD EVENTS
        	$(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".ui-hp .upcomingevents .ui-articles");
		if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
			$("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
		}
		//CHANGE VIEW CALENDAR LINK TEXT
		$(".upcomingevents .view-calendar-link span").text("VIEW MORE>>");

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);
		// SCROLL TO TOP BUTTON
		$("#scroll-top").click(function(e){
			e.preventDefault();
			$("html, body").animate({ scrollTop: 0 }, "slow");
			return false;
		});
		// ELEMENT footerLogoToggle SHOWS OR HIDES THE FOOTER LOGO
		var footerLogoToggle = 'true'; 
		if(footerLogoToggle == "true"){
			$('').prependTo(".gb.footer.one");
		}	
		// ELEMENT socialToggleFooter & socialToggleHeader SHOWS OR HIDES THE FOOTER LOGO
		var socialToggleFooter = 'true'; 
		if(socialToggleFooter == "false"){
			$("#gb-footer .social-icons").hide();
		}
		var socialToggleHeader = 'true';
		if(socialToggleHeader == "false"){
			$(".header.right .social-icons").hide();
		}
		var facebookToggle = 'true';
		if(facebookToggle == "false"){
			$(".facebook").hide();
		}
		var twitterToggle = 'true';
		if(twitterToggle == "false"){
			$(".twitter").hide();
		}	
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "SEARCH");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "SEARCH") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("SEARCH");
			}
		});
	}

