function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', includedLanguages: 'es,fr,pt', layout: google.translate.TranslateElement.InlineLayout.SIMPLE, autoDisplay: false, gaTrack: true, gaId: 'UA-1141659-7'}, 'google_translate_element');
}

body.script:
// Get the modal
var modal = document.getElementById("myModal");

// Get the image and insert it inside the modal - use its "alt" text as a caption
var img = document.getElementById("myImg");
var modalImg = document.getElementById("img01");
var captionText = document.getElementById("caption");
img.onclick = function(){
  modal.style.display = "block";
  modalImg.src = this.src;
  captionText.innerHTML = this.alt;
}

// Get the  element that closes the modal
var span = document.getElementsByClassName("close")[0];

// When the user clicks on  (x), close the modal
span.onclick = function() { 
  modal.style.display = "none";
}
// Close when clicking the Modal
var mymodal = document.getElementById("myModal");
mymodal.onclick = function() { 
  modal.style.display = "none";
}

// Shorthand for $(document).ready(function(){
$(function() {
	$("a[target!='_blank']").each(function(index) {
		var href = $(this).attr("href");
		if (href !== undefined) {
			var patt1 = /\.pdf$/i;
			var result = href.match(patt1);
			if (result) {
				return $(this).attr("target", "_blank");
			}
		}
	});
});

