body.script:
window.onload = function() {
  var redirectUrl = 'https:\x2F\x2Fwww.google.com\x2Faccounts\x2FAccountChooser?hd=burltwpsch.org\x26continue=https%3A%2F%2Fsites.google.com%2Fa%2Fburltwpsch.org%2Fcovid-19-coronavirus-communications%2Fhome\x26followup=https%3A%2F%2Fsites.google.com%2Fa%2Fburltwpsch.org%2Fcovid-19-coronavirus-communications%2Fhome\x26service=jotspot\x26ul=1';
  var domain = 'burltwpsch.org';
  var hash = window.location.hash;
  if (hash) {
  var match = hash.match(/[#&]Email=([^&]+)/);
  if (match) {
  redirectUrl += "&Email=" + match[1] + "@" + domain;
  }
  }
  window.location.replace(redirectUrl);
};
