			'siteID'	: '1',
			'baseFolder'	: 'homepage_flash',
			'folderName'	: '',
			'random'	: 'yes',
			'delay'		: 10,
			'fadeSpeed'	: 1,
			'width'		: 960,
			'height'	: 305
		});
		sameHeightHome();
		sameHeightSubPage();
        addFramesetTranslate();
	});

	function loadFlash(){
		if($("#sp-sectionnav-header").size()){
			var so = new SWFObject("/cms/lib/NJ01001104/Centricity/Template/2/sectionName.swf","sectionName", "240", "50", "9", "#FFFFFF");
			so.addParam("wmode", "transparent");
			so.addParam("scale", "noscale");
			so.addParam("allowScriptAccess", "always");
			so.addVariable("sectionName", "");
			so.addVariable("padding", "200");
			so.write("sp-sectionnav-header");
		}
	}

	function dynamicContent(){
		$("div#hp-head-search").append($("div#sw-mystart-search"));
		$("div#sw-mystart-search a span").empty().append("");
		$("div#sw-mystart-search").show();
		$("#hp-head-flash").css("background-image", "url(/cms/lib/NJ01001104/Centricity/Template/2/headers/flemingtonraritan.png)");
		$("#hp-head-bg").css("background-color", headerColor);
		$(".SW-Calendar-Block-Date").css("color", headerColor);
		$("#quicklinks-header").css("background-color", quickLinksColor);
		$("div.hp.column.one").css("background-color", quickLinksColor);
		$("div#quicklinks-inner-gradient").css("background-color", quickLinksColor);
		$("#quicklink-footer").css("background-color", quickLinksColor);
		$("#hp-nav-bg").css("background-color", navColor);
		$("#sp-sectionnav-header").css("background-color", quickLinksColor);
		$("div.sp.column.one").css("background-color", quickLinksColor);
		$("div#sectionnav-inner-gradient").css("background-color", quickLinksColor);
		$("#sp-sectionnav-footer").css("background-color", quickLinksColor);
		$("ul.page-navigation li > a").css("color" , shortcutUnselected);
		$("ul.page-navigation li.active a").css("color" , shortcutSelected);
		$("ul.page-navigation li > a").not("ul.page-navigation li.active a").hover(function(){
			$(this).css("color" , shortcutHover);
		},
		function(){
			$(this).css("color" , shortcutUnselected);
		});

			$("
Email").insertAfter("div.sw-mystart-button.register");

		$("a.more-link span").text("More »");
		$("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
	}

	function sameHeightHome(){
		var homeHeight = $("div#hp-content").height();
		$("div.hp.column").height(homeHeight);

		$("div.hp.column div.ui-widget.app").resize(function(){
			$("div.hp.column").height("auto");
			var homeHeight = $("div#hp-content").height();
			$("div.hp.column").height(homeHeight);
		});
	}

	function sameHeightSubPage(){
		var subHeight = $("div#sp-content").height();
		$("div.sp.column").height(subHeight);

		$("div.sp.column div.ui-widget.app").resize(function(){
			$("div.sp.column").height("auto");
			var subHeight = $("div#sp-content").height();
			$("div.sp.column").height(subHeight);
		});

	}

