    function EventDetailExportByDateRange() {
        var eventDate = new Date($('#eventexport-txt-startdate').val());
        var endDate = new Date($('#eventexport-txt-enddate').val());
                
        if (endDate < eventDate) {
            swalert('The start date must come before the end date.', 'Attention', 'warning', 'ok');
            return false;
        }

        document.location.href = "https://www.frsd.k12.nj.us//site/UserControls/Calendar/EventExport.aspx?ModuleInstanceID=874&StartDate=" + $('#eventexport-txt-startdate').val() + "&EndDate=" + $('#eventexport-txt-enddate').val();
        CloseDialogOverlay('WindowMediumModal');
    }

    function getFullMonth(monthValue) {
        monthValue = parseInt(monthValue);
        var fullMonth = "";

        switch(monthValue) {
            case 1:
                fullMonth = "January";
                break;
            case 2:
                fullMonth = "February";
                break;
            case 3:
                fullMonth = "March";
                break;
            case 4:
                fullMonth = "April";
                break;
            case 5:
                fullMonth = "May";
                break;
            case 6:
                fullMonth = "June";
                break;
            case 7:
                fullMonth = "July";
                break;
            case 8:
                fullMonth = "August";
                break;
            case 9:
                fullMonth = "September";
                break;
            case 10:
                fullMonth = "October";
                break;
            case 11:
                fullMonth = "November";
                break;
            case 12:
                fullMonth = "December";
                break;
        }

        return fullMonth;
    }
    
    function SearchMyEvents() {
        var StartDate;
        var EndDate;

        if ($('#myeventslist-txt-startdate').val() == '') {
            StartDate = '1/1/1900';
        } else {
            StartDate = $('#myeventslist-txt-startdate').val();
        }

        if ($('#myeventslist-txt-enddate').val() == '') {
            EndDate = '1/1/1900';
        } else {
            EndDate = $('#myeventslist-txt-enddate').val();
        }
        
        if ((StartDate != '1/1/1900') && (EndDate != '1/1/1900')) {
            if (new Date(StartDate) > new Date(EndDate)) {
                swalert('The start date must come before the end date.', 'Attention', 'warning', 'ok', '', '', '');
                return false;
            }
        } else if ((EndDate != '1/1/1900') && (StartDate == '1/1/1900')) {
            var CurrentDate = new Date();

            StartDate = (parseInt(CurrentDate.getMonth()) + 1) + "/" + CurrentDate.getDate() + "/" + CurrentDate.getFullYear();

            if (new Date(StartDate) > new Date(EndDate)) {
                swalert('The end date must come after the current date.', 'Attention', 'warning', 'ok', '', '', '');
                return false;
            }
        }
              
        if (StartDate != '1/1/1900') {
            StartDate += ' 0:00:00';
        }

        if (EndDate != '1/1/1900') {
            EndDate += ' 23:59:59';
        }

        GetContent("https://www.frsd.k12.nj.us//site/UserControls/Calendar/MyEventsListWrapper.aspx?ModuleInstanceID=874&StartDate=" + StartDate + "&EndDate=" + EndDate, "myeventslist-pnl-detail", 2, '');
    }

    function RefreshCalendarFilter() {
        if ($('#calendar-pnl-calendarfilter-closed').is(':visible')) {
            GetContent("https://www.frsd.k12.nj.us//site/UserControls/Calendar/CalendarFilterListWrapper.aspx?ModuleInstanceID=" + "874" + "&Expanded=" + false, "calendar-pnl-calendarfilter-closed", 2);
        } else {
            GetContent("https://www.frsd.k12.nj.us//site/UserControls/Calendar/CalendarFilterListWrapper.aspx?ModuleInstanceID=" + "874" + "&Expanded=" + true, "calendar-pnl-calendarfilter-open", 2);             
        }
    }
    
    function ToggleCalendarFilter(expanded) {
        if (expanded === undefined) {
            expanded = $('#calendar-pnl-calendarfilter-closed').is(':visible');
        }

        if (expanded === true) {
            var callback = "$('#calendar-pnl-calendarfilter-closed').fadeOut('fast',function() { $('#calendar-pnl-calendarfilter-open').fadeIn('fast');});";
            GetContent("https://www.frsd.k12.nj.us//site/UserControls/Calendar/CalendarFilterListWrapper.aspx?ModuleInstanceID=" + "874" + "&Expanded=" + true, "calendar-pnl-calendarfilter-open", 3, callback);
        } else {
            var callback = "$('#calendar-pnl-calendarfilter-open').fadeOut('fast',function() { $('#calendar-pnl-calendarfilter-closed').fadeIn('fast');});";
            GetContent("https://www.frsd.k12.nj.us//site/UserControls/Calendar/CalendarFilterListWrapper.aspx?ModuleInstanceID=" + "874" + "&Expanded=" + false, "calendar-pnl-calendarfilter-closed", 3, callback); 
        }
    }

