    }
    } else {
        function showHideComments(view) {
            $("#calendar-pnl-calendarcontainer-874 .app-level-social-commenting")[view === 'event' ? 'hide' : 'show']();
            $("#calendar-pnl-calendarcontainer-874 .app-level-social-rating")[view === 'event' ? 'hide' : 'show']();
        }

        $("#calendar-pnl-calendarcontainer-874 .app-level-social-commenting, #calendar-pnl-calendarcontainer-874 .app-level-social-rating").hide();

        Bb.WCM.UI.Calendar('874', { attachTo: document.getElementById('calendar-newplaceholder-874'), 
            name: '', customized: true, pageID: '2', domainID: '1', isUserLogged: false, showRssBtn: true})
        .then (
            function (cal) {
                showHideComments(cal.view);
                cal.on("viewChange", function (view) {
                    showHideComments(view);
                });
                cal.on("interaction", function (category, action, label) {
                    if (!window.AddAnalyticsEvent) {
                        var loadAttempts = 0;
                        var checker = setInterval( function () {
                            loadAttempts++;
                            if (window.AddAnalyticsEvent) {
                                AddAnalyticsEvent(category, action, label);
                                clearInterval(checker);
                            }
                            if (loadAttempts >= 10) {
                                clearInterval(checker);
                            }
                        }, 300);
                    } else {
                        AddAnalyticsEvent(category, action, label);
                    }
                });
            }
        );
    }

