          if($(this).attr('value') == "S"){
             $(this).remove();
          }
        });
        //End Case 00236151
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		if(!isMSIE){ imgResize(); }
		searchText();
		popularLinks();
	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE7){ $("body").addClass("msie7"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize(); imgResize(); adjustHeader(); });

		// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"menuButtonParent"	: "#sw-channel-list-container",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MAIN MENU",
			"showSchools"		: "no",
			"showTranslate"		: "no",
			"translateId"		: "none",
			"showAccount"		: "yes",
			"extraMenuOptions"	: "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"			: "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
			"breakPoint"		: 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"menuButtonParent"	: "#sp-content",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "PAGES",
			"siteID"			: "4"
		});

		pgScreenSize();
		useChannelForSection();
		dynamicContent();
		addFramesetTranslate();	
		appScroller();
		adjustHeader();	
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			mediaRotator();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			mediaRotator();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			mediaRotator();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			mediaRotator();
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			mediaRotator();
			appView();
			if(!$("#gb-channel-list #gb-pop-icons-selector").length) {
				if($("#hp-pop-icons").length) {
					var popLinks = $("#hp-pop-icons").clone();
					$("#gb-channel-list").prepend(popLinks.html());
				} else {
					var popLinks = $("#gb-pop-icons-selector").clone();
					$("#gb-channel-list").prepend(popLinks);
				}
			}
		}
	}

	function mediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth" 			: 1600,
		        	"imageHeight" 			: 1073,
		        	"appHeader" 			: "no",
		        	"titles" 			: "yes",
		        	"captions" 			: "yes",
		        	"galleryOverlay" 		: "no",
		       		"linkedElement" 		: ["image"], // ["image", "title", "overlay", "all"]
		        	"playPause" 			: "no",
		        	"mainControls" 			: "no",
		        	"contMoreLink" 			: "no",
		        	"contMoreLinkText" 		: "Read More",
		        	"descMoreLink" 			: "no",
		        	"descMoreLinkText" 		: "Read More",
		        	"bullets" 			: "yes",
		        	"bulletControls" 		: "no",
		        	"thumbnails" 			: "no",
		        	"thumbnailWidth" 		: 122,
		        	"thumbnailHeight" 		: 53,
		        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
		        	"hoverPause" 			: "yes",
		        	"fullScreenRotator" 		: "yes", // image dimensions work well at 1500x915
		        	"fullScreenBreakpoints"		: ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		        	"fullScreenVideo" 		: "no",
		        	"transitionType" 		: "fade", // fade or slide
		        	"transitionSpeed" 		: 3,
		        	"transitionDelay" 		: 4,
				"autoRotate" 			: "yes",
		        	"onImageLoad" 			: function() { },
		        	"allImagesLoaded" 		: function() { },
		        	"onThumbnailLoad" 		: function() { },
		        	"allThumbnailsLoaded" 		: function() { },
		        	"onTransitionStart" 		: function() { },
		        	"onTransitionEnd" 		: function() { },
		        	"watchVidClick" 		: function() { },
		        	"closeVidClick" 		: function() { },
		        	"allLoaded" 			: function() { }
			});
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		//if(!$("div.ui-widget.app.active").length){
			//$("#hp-content div.ui-widget.app:first").addClass("active");
		//}
		$("div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function adjustHeader() {
		if($(window).width() < 480) {
			$("#hp-content-outer").css("padding-top", $("#gb-channel-list-outer").outerHeight(true) + "px");
		} else {
			$("#hp-content-outer").removeAttr("style");
		}
	}

	function appScroller() {
		$("#sw-content-container1.ui-hp div.ui-widget.app.headlines").appScroller({
	                "appParent"             : ".hp",
	                "targetApp"             : "headlines", //upcomingevents or headlines
	                "imageNum960"           : 4, //NUMBER OF IMAGES TO DISPLAY AT 960
	                "imageNum768"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 768
	                "imageNum640"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 640
	                "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
	                "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
	                "backIconFontLetter"    : "A", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
	                "nextIconFontLetter"    : "A", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
	                "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
	                "extraBackIconClass"    : "sw-arrow",
	                "extraNextIconClass"    : "sw-arrow",
	                "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
	        });

	        $("#sw-content-container1.ui-hp div.ui-widget.app").each(function() {
