	function SWEndPreviewMode() { 
		var data = "{}";
		var success = "window.location='';";
		var failure = "CallControllerFailure(result[0].errormessage);";
		CallController("https://www.mahwah.k12.nj.us/site/SiteController.aspx/EndPreviewMode", data, success, failure);
	}
	
