	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$("#hp-app-right  div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-hp div.ui-widget-header, .ui-hp #hp-app-right div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500);
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();
		//MOVE SCHOOL DROPDOWN TO GB-MYSTART
		$(".sw-mystart-dropdown.schoollist").prependTo("#gb-mystart");
		$("#sw-mystart-mypasskey").appendTo("#gb-mystart");
		//ADD Class last to center column last app so It can be rounded.
		$(".hp.column.two div.ui-widget.app:last").addClass("last");
		$(".hp.column.two div.ui-widget.app:not(div.ui-widget.app:first)").addClass("others");

		//ADD TRANSLATE
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");

		if($(".sw-mystart-button.pw").length) {
			$("#sw-mystart-search").before($(".sw-mystart-button.pw"));
		}

		//DOC #swsignin-txt-username -- AUTO-FOCUS USERNAME SIGNIN FORM FIELD #swsignin-txt-username
		$("#swsignin-txt-username").focus();

		//DOC if myview button exists, move into myaccount dropdown
		if($(".sw-mystart-button.pw").length) {
			var myviewBtn = $(".sw-mystart-button.pw").html();
			$("
" + myviewBtn + "").prependTo("#sw-myaccount-list");
			$(".sw-mystart-button.pw").hide();
		}


		// ********** HEADER ********** //
		// #gb-logo -- Checks to see if a logo has been selected, displays default logo until one has been selected.
		var showLogo = 'true';
		var templateLogoPath = '/cms/lib/NJ01912812/Centricity/Template/GlobalAssets/images///logos/MBS.jpg';
		if (templateLogoPath.indexOf("default-man.jpg") != -1 || templateLogoPath == ""){
			$("#gb-logo a img").attr("src", '//extend.schoolwires.com/creative/subscription_library/myway_premium/dynamically_branded/logo/rwd.png');
		}
		else {
			$("#gb-logo a img").attr("src", templateLogoPath);
		}
		// #gb-logo  showSchoolLogo SHOWS OR HIDES SCHOOL LOGO
		if(showLogo == "true") {
			$("#gb-logo").show();
		}
		if($("#gb-sitename h1 a").text() == "" && showLogo == 'true') {
			$("#gb-logo").show();
		}
		// ********** BACKGROUND IMAGE *******************************//

		var showBackground = 'true';
		var backgroundPath = '/cms/lib/NJ01912812/Centricity/Template/GlobalAssets/images///background-images/Web_Background_Beach.jpg';
		if (backgroundPath.indexOf("default-man.jpg") != -1 || backgroundPath == ""){
			$("#background-image img").attr("src", '//extend.schoolwires.com/creative/subscription_library/myway_premium/dynamically_branded/background_image/dynamicallyBranded.jpg');
		}
		else {
			$("#background-image img").attr("src", backgroundPath);
		}
		if(showBackground == "true") {
			$("#background-image").show();
		}



		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		$(".headlines a.more-link span").text("VIEW ALL HEADLINES");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}

        	//MOD EVENTS
        	$(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".ui-hp .upcomingevents .ui-articles");
		if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
			$("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
		}
		var opacityColor = ("rgba("+hexToRgb('#3C95EE')+",0.1)");
		$("div.ui-widget.app.upcomingevents div.upcoming-column-container div.upcoming-column.left").css("background" , opacityColor);

		//DOC #sp-breadcrumbs -- ELEMENT breadcrumbs SHOWS OR HIDES BREADCRUMBS
        	var breadcrumbs = 'false';
		if(breadcrumbs == "false"){
			$("#sp-breadcrumbs").hide();
		}

		//ADD CANVAS FOR COLUMN TWO HEADER
		$('').prependTo(".hp.column.two .ui-widget-header");

		//ALERT
        	if($("#hp-alert .ui-article").size() > 0) {
        		$("#hp-alert").show();
        	}

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	

		//DISCLAIMER
		var disclaimerToggle = 'false';
		switch(disclaimerToggle) {
			case"false":
				$("#footer-disclaimer").hide();
			break;
			case"true":
				$("#footer-disclaimer").show();
			break;
		}		
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function imgResize(){
		var imgHeight = $("#hp-slideshow .rotating-images img").height();
		$("#hp-slideshow").height(imgHeight);
	}

