          if (jQuery(window).scrollTop() > jQuery('body').height() / 2) {
            jQuery('#ppm-popup-13141').addClass('ppm-popup-wrapper-activate');
          } 
        });





    } else {


        // Popup is showing first time!
        jQuery(window).scroll(function () { 
          if (jQuery(window).scrollTop() > jQuery('body').height() / 2) {
            jQuery('#ppm-popup-13141').addClass('ppm-popup-wrapper-activate');
          } 
        });  



    }



    var expiresAt = new Date();

