link.script:
    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.MyStart();
            this.Translate();
            this.Header();
            this.ChannelBar();
            this.Body();
            this.Footer();
            this.RsMenu();
            this.Search();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($(".myview").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "MyStart": function() {
            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

            // ADJUST SYSTEM PAGES FOR CONSISTENCY
            $("#myaccountedit-pnl-main, .ui-widget.error-404").addClass("app");
        },

        "Translate": function() {
        	// FOR SCOPE
            var _this = this;

        	// ADD TRANSLATE
        	$(".cs-mystart-dropdown.translate .cs-dropdown").creativeTranslate({
                "type": 2,
                "advancedOptions": {
                    "addMethod": "append",
                    "brandedLayout": 1
                },
                "translateLoaded": function() {
                	$(".cs-mystart-dropdown.translate").removeClass("hidden");

                    // BIND DROPDOWN EVENTS
                    _this.DropdownActions({
                        "dropdownParent": ".cs-mystart-dropdown.translate",
                        "dropdownSelector": ".cs-selector",
                        "dropdown": ".cs-dropdown",
                        "dropdownList": ".cs-dropdown-list"
                    });
                }
            });
        },

        "DropdownActions": function(params) {
            /* ASSUMES THE FOLLOWING STRUCTURE



User Options




Link



            */

        	// FOR SCOPE
            var _this = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.enter:
                    case _this.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.down:
                    case _this.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case _this.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case _this.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case _this.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(dropdownSelector).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;
                }
            });

            var dropdownTimer;
            $(dropdownParent).mouseenter(function() {
            	clearTimeout(dropdownTimer);
            }).mouseleave(function() {
            	var thisDropdownParent = this;

				dropdownTimer = setTimeout(function(){
                	if($(thisDropdownParent).find("#google_translate_element").length) {
                    	if(!$(thisDropdownParent).find("#cs-branded-translate-label").is(":focus")){
                        	$(dropdownList + " a", this).attr("tabindex", "-1");
                            $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        } else {
                        	$("#cs-branded-translate-label").on("change", function() {
								$(".goog-logo-link").focus();
                            });
                        }
                    } else {
                		$(dropdownList + " a", thisDropdownParent).attr("tabindex", "-1");
						$(dropdownSelector, thisDropdownParent).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            		}
                }, 500);
            }).focusout(function() {
                var thisDropdownParent = this;

                setTimeout(function () {
                    if(!$(thisDropdownParent).find(":focus").length && !$(thisDropdownParent).find("#google_translate_element").length) {
                        $(dropdownSelector, thisDropdownParent).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });

            $(".cs-mystart-dropdown:not(.translate) .cs-selector").focus(function() {
            	$(".cs-mystart-dropdown.translate .cs-selector").attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
            });
        },

        "Header": function() {
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/NJ50000547/Centricity/Template/GlobalAssets/images///logos/NBPS Embossed black 50x50 new.png");
            if(showLogo) {
                if((logoSrc != "") && logoSrc.indexOf("default-man") == -1) {
                    $("#gb-logo").append('');
                    $("#gb-header").attr("data-has-logo", "true");
                }
            } else {
                $("#gb-logo").remove();
            }

            // SHOW SITENAME
            var showSiteName = true;
            if(showSiteName) {
            	$("#gb-sitename h2").removeClass("hidden");
                $("#gb-header").attr("data-has-sitename", "true");
            } else {
            	$("#gb-sitename h2").remove();
            }

            // SHOW TAGLINE
            var showTagline = true;
            if(showTagline) {
            	$("#gb-sitename p").removeClass("hidden");
                $("#gb-header").attr("data-has-tagline", "true");
            } else {
            	$("#gb-sitename p").remove();
            }

            if(!showSiteName && !showTagline) {
            	$("#gb-sitename").remove();
            }
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-dropdown").each(function() {
            	if(!$("> li", this).length) {
                	$(this).remove();
                }
            });
        },

        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            // ADJUST FOLLOW LINKS FOR CONSISTENCY
            $(".app-level-social-follow").each(function() {
            	if(!$(this).closest(".ui-widget-footer").length) {
                	$(this).closest(".ui-widget.app").find(".ui-widget-footer").prepend($(this));
                }
            });

            // ADJUST EVENTS
            $(".ui-widget.app.upcomingevents .ui-article").each(function() {
				if (!$(".ui-article-title.sw-calendar-block-date", this).length && $.trim($(".ui-article-description", this).text()) != "There are no upcoming events to display.") {
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".ui-article").append(moveArticle);
					$(this).parent().remove();
				}
			});

            // NAVIGATION APP HOVER
            $(document).on("mouseenter focusin", "div.ui-widget.app.navigation div.ui-widget-detail > ul > li", function() {
            	$(this).addClass("hover");
            }).on("mouseleave focusout", "div.ui-widget.app.navigation div.ui-widget-detail > ul > li", function() {
            	$(this).removeClass("hover");
            });

            // UPCOMING EVENTS - MOVE EVENT TIME TO DISPLAY AFTER THE TITLE
            $(".upcomingevents .ui-article-description").each(function() {
            	var thisEvent = this;
                if($(".sw-calendar-block-time",thisEvent).length) {
                	$(thisEvent).find(".sw-calendar-block-time").appendTo(thisEvent);
                }
            });
        },

