function resizeSlider_ir3796() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("0");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir3796-ir3796-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir3796-ir3796-slider").style.height = Math.max((parseFloat("320")/parseFloat("960") * containerWidth), parseFloat("320")) + "px";
	document.getElementById("ir3796-ir3796-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

