link.script:
function waitForElement() {
    if (typeof videojs !== "undefined") {
        videojs.options.flash.swf = "//vjs.zencdn.net/5.8.0/video-js.swf";
        videojs('video', {}, function () {
            videojs.ga();
        });
    }
    else {
        setTimeout(function () {
            waitForElement();
        }, 1);
    }
}

            // When the user scrolls down 20px from the top of the document, show the button
            window.onscroll = function () { scrollFunction() };

            function scrollFunction() {
                if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
                    document.getElementById("myBtn").style.display = "block";
                } else {
                    document.getElementById("myBtn").style.display = "none";
                }
            }

            // When the user clicks on the button, scroll to the top of the document
            function topFunction() {
                document.body.scrollTop = 0; // For Chrome, Safari and Opera
                document.documentElement.scrollTop = 0; // For IE and Firefox
            }

    window.dataLayer = window.dataLayer || [];
    function gtag() { dataLayer.push(arguments) };
    gtag('js', new Date());

    gtag('config', 'UA-107452056-1');

div.script:
function popUp(URL) {
        day = new Date();
        id = day.getTime();
        eval("page" + id + " = window.open(URL, '" + id + "', 'toolbar=0,scrollbars=1,location=0,statusbar=1,menubar=0,resizable=1,width=700,height=600,left = 440,top = 212');");
    }

link.script:
$('#navigation').slicknav({
    label: 'Menu',
    prependTo: '#mobile_top_nav',
    allowParentLinks: true,
});
var navTop = $("nav").offset().top
$(window).scroll(function(){
    if (navTop <= $(window).scrollTop()){
        $("nav").css("position", "fixed");
        $("nav").css("top", "0");
    }else{
        $("nav").css("position", "static");
    }
});

div.script:
function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE
    }, 'google_translate_element');
}

link.script:
function QuickLinks() {
    $('.expand-one').click(function () {
        $('.content-one').slideToggle('slow');
        switch ($('.Toggle').attr('class')) {
            case 'Toggle PlusImage':
                $('.Toggle').addClass('XImage');
                $('.Toggle').removeClass('PlusImage')
                break;
            case 'Toggle XImage':
                $('.Toggle').addClass('PlusImage');
                $('.Toggle').removeClass('XImage')
                break;
            default: $('.Toggle').addClass('XImage');
                break;
        }
    });
}
$(document).ready(function () {
    QuickLinks();
});
