    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "HeaderIsSticky": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.DynamicBackgrounds();            
            this.JsMediaQueries();
            this.MyStart();
            this.Header();
            this.RsMenu();
            this.Search();
            this.ChannelBar();
            this.Body();
            if($(".hp").length) {
            	this.Slideshow();
                this.Programs();
                this.PhotoCollage();
                this.Stats();
                this.Schools();
            }
            this.UpcomingEvents();
            this.AppAccordion();
            this.Footer();

            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){ _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.StickyHeader(); 
            this.JsMediaQueries();
        },

        "WindowResize": function() {

        },

        "WindowScroll": function() {   
            // FOR SCOPE
            var _this = this;

            this.StickyHeader();

        	if($('.cs-homepage-stats').hasClass('needs-animated')){
                var sT = $('.cs-homepage-stats').offset().top
                    wS = $(window).scrollTop();
                    wH = $(window).height();

                if (wS >= (sT - (wH * 0.5))){
                   _this.AnimateStatNumbers();
                   $('.cs-homepage-stats').removeClass('needs-animated');
                }
        	}
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":
                case "640":

                break;
                case "480":
                case "320":
					$(".hp-row div.ui-widget.app").each(function() {
                    	if($(".ui-widget-header > .more-link", this).length) {
                        	var detailPage = $(".ui-widget-header > .more-link", this).attr("href");
                        	$(".ui-widget-header", this).off();
                            $(".ui-widget-header", this).on("click", function() {
                            	window.location = detailPage;
                            });
                        }
                    });
                break;
            }
        },

        "StickyHeader": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("header").outerHeight();
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.HeaderIsSticky) {
                                $("header").removeClass("fixed-element").removeAttr("style");
                                $("#gb-page").css("padding-top", 0);

                                this.HeaderIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("header").css("height", $("header").outerHeight(true));
                            $("#gb-page").css("padding-top", $("header").outerHeight(true));

                            if(!this.HeaderIsSticky) {
                                $("header").addClass("fixed-element");

                                this.HeaderIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("nav").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.HeaderIsSticky) {
                            $("header").removeClass("fixed-element").removeAttr("style");
                            $("#gb-page").css("padding-top", 0);

                            this.HeaderIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "DynamicBackgrounds": function() {
        	//Variables
            var regionBCBGSrc = $.trim('/cms/lib/NY01913517/Centricity/Template/GlobalAssets/images///2019 Background/baldwinschools-background-classroom.jpg');
            var srcCheck = regionBCBGSrc.indexOf("Faces/default-man.jpg") > -1;
            var regionCBGSrc = $.trim('/cms/lib/NY01913517/Centricity/Template/GlobalAssets/images///2019 Background/baldwin-schools-baldwin-hs-redesigned-classroom-BG-4.jpg');
            var srcCheck2 = regionCBGSrc.indexOf("Faces/default-man.jpg") > -1;
            var regionEFBGSrc = $.trim('/cms/lib/NY01913517/Centricity/Template/GlobalAssets/images///2019 Background/baldwin-schools-shadow-day-v1.jpg');
            var srcCheck3 = regionEFBGSrc.indexOf("Faces/default-man.jpg") > -1;
            var statsBGSrc = $.trim('/cms/lib/NY01913517/Centricity/Template/GlobalAssets/images///2019 Background/baldwin-schools-longisland-ny-3.jpg');
            var srcCheck4 = statsBGSrc.indexOf("Faces/default-man.jpg") > -1;            
            var styles = "";

            if((regionBCBGSrc != "") && (!srcCheck)) {
                styles += '.hp-row.one:before { \n'+   
                              "\t background: fixed url('"+regionBCBGSrc+"') no-repeat; \n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            } else {
                styles += '.hp-row.one:before { \n'+   
