            } else if(siteName != "") {
                $("#gb-sitename").prepend("
" + siteName + "");
            }
        },

        "ChannelBar": function() {
            // FOR SCOPE
            var _this = this;

        	$(".hidden-sections").remove();
            $(".sw-channel-dropdown").hide();

            $(".sw-channel-item").each(function(){
                var channels = $(".sw-channel-dropdown li", this);
                for(var i = 0; i < channels.length; i+=5) {
                  channels.slice(i, i+5).wrapAll("");
                }
            });

            $(".sw-channel-dropdown").wrapInner("
");

            $(".sw-channel-column a").unbind();

            $(document).on("keydown",".sw-channel-column a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                	// CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();
                        if($(this).parents(".sw-channel-dropdown").find("a").index(this) == 0) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                        	var index = $(this).parents(".sw-channel-dropdown").find("a").index(this)-1;
                        	$(this).parents(".sw-channel-dropdown").find("a").eq(index).focus();
                        }
                    break;      
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(this).parents(".sw-channel-dropdown").find("a").index(this) == $(this).parents(".sw-channel-dropdown").find("a").length-1) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $(this).parents(".sw-channel-dropdown").find("a").index(this)+1;
                            $(this).parents(".sw-channel-dropdown").find("a").eq(index).focus();
                        }
                    break;
                }
            });

            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").on('mouseenter mouseleave', function (e) {
                if ($('.sw-channel-dropdown', this).length) {
                    var elm = $('.sw-channel-dropdown', this);
                    var off = elm.offset();
                    var l = off.left;
                    var w = elm.width();
                    var docH = $("header").height();
                    var docW = $("header").width();

                    var isEntirelyVisible = (l + w <= docW);

                    if (!isEntirelyVisible) {
                        $(this).addClass('edge');
                    } else {
                        $(this).removeClass('edge');
                    }
                }
            });
        },

