function ajaxContent(requestUrl){
	$('#sw-ajaxData').html('');
	$('#sw-ajaxData').addClass('loading');
	if(requestUrl != undefined && requestUrl != null && requestUrl != '' && requestUrl != '#'){

		$.ajax({url: requestUrl,
			success: function(data) { 
				$('#sw-ajaxData').removeClass('loading');
				$('#sw-ajaxData').html(data);
			},
			error: function(){
				$('#sw-ajaxData').html('CRITICAL FAILURE');
			},
			cache:false
		});//$.ajax
	}
}//ajaxContent



$(function(){

//get files ajax stuff
$('a.sw-ajaxLink').live('click', function(){
	var curEle = $(this);
	var xhrUrl = curEle.attr('href');
	
	ajaxContent(xhrUrl);
	
	//prevent normal link action
	return false;
});



});//document ready

           
