function disableSubmit()
{
	document.myForm.submitButton.value = "Please Wait...";
	document.myForm.submitButton.disabled = true;	
}

function enableSubmit()
{
	
		document.myForm.submitButton.value = "Submit Form >";		
	
		document.myForm.submitButton.disabled = false;
}

function prepareSubmitButton() {
	var bodyElemArray = document.getElementsByTagName("body");
	if (bodyElemArray.length > 0) {
		var previousValue = bodyElemArray[0].getAttribute("onpageshow");
		var newValue = "enableSubmit();";
		if (previousValue != null) {
			newValue += previousValue;
		}
		bodyElemArray[0].setAttribute("onpageshow", newValue);		
	}
}

prepareSubmitButton();



