	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
    var hpAlert = false;

    $(window).scroll(function() {
    	var headerHeight = $("#gb-header-outer").height();
        var mystartHeight = $("#gb-mystart-outer").height();
        var scrollTop = $(window).scrollTop();
        var totalHeight = headerHeight + mystartHeight;
        var channelHeight = $("#gb-channel-list-outer").height();

    	if($("#hp-alert .ui-widget.app .ui-article").length) {
        	hpAlert = true;
            var alertHeight = $("#hp-alert-outer").height();
            totalHeight = totalHeight + alertHeight;
        }

        if($(window).width() > 1023) {
          if(scrollTop > totalHeight) {
			$("#gb-channel-list-outer, #gb-channel-list .cs-special-channels").addClass("sticky");
            $("#gb-channel-list .cs-special-channels li a").attr("tabindex","0");
            $("#hp-slideshow, #sp-content-outer, #spn-content-outer").css("margin-top",channelHeight);

            if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css({
                    	"margin-top": $("#gb-channel-list-outer").outerHeight(true),
                    	"padding-top": "48px"
                    });
                    $(".wcm-calendar .wcm-calendar-viewpicker").css({
                    	"top": "auto",
                        "bottom": "0"
                    });
                }
            }

            var stickyHeight = $("#gb-channel-list-outer").outerHeight() + 48;
            $("#sw-maincontent").css({
                  "display": "block",
                  "position": "relative",
                  "top": "-" + stickyHeight + "px"
              });
          } else {
          	$("#gb-channel-list-outer, #gb-channel-list .cs-special-channels").removeClass("sticky");
            $("#gb-channel-list .cs-special-channels li a").attr("tabindex","-1");
            $("#hp-slideshow, #sp-content-outer, #spn-content-outer").css("margin-top","0");
          }
        }
    });

	$(window).load(function() {


    	//REMOVE EXTRA HEADER IMAGE IF NOT IN USE
        $(".extra-header-image.use-image-false").remove();
        //REMOVE SOCIAL ICON IF NOT IN USE
    	$(".social-icon.false").remove();
        if(!$(".social-icon.true").length) {
        	$(".gb-social-icons").remove();
        }

		searchText();

        //ACCESSIBLE NAV APP
        $(".ui-widget.app.navigation").csAccessibleNavigationApp();

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.stop(true,true).slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

        MyStartDropdownActions({
            "dropdownParent": ".sw-mystart-dropdown",
            "dropdownSelector": ".selector",
            "dropdown": ".sw-dropdown",
            "dropdownList": ".sw-dropdown-list"
        });

        if($(".ie11").length) {
        	$("#gb-mystart .sw-mystart-dropdown.translate").off("hover");
        }

        $("#channel-navigation .sw-channel-item.mega-channel:eq(2), #channel-navigation .sw-channel-item.mega-channel:eq(3), #channel-navigation .sw-channel-item.mega-channel:eq(4)").addClass("center-justify"); /* moved eq(4) to this line from RIght-justify by Matt - case#3141766 - 05.07.18*/
        $("#channel-navigation .sw-channel-item.mega-channel:eq(5), #channel-navigation .sw-channel-item.mega-channel:eq(6), #channel-navigation .sw-channel-item.mega-channel:eq(7), #channel-navigation .sw-channel-item.mega-channel:eq(8)").addClass("right-justify");

    	csGlobalJs.OpenInNewWindowWarning();
    });

