    function socialTabs() {
    	$(".social-media-tabs #sw-content-container7 > div").after("
");
        $(".social-media-tabs .ui-widget-header").addClass("tab-button");
        $(".social-media-tabs .ui-widget-header h1").addClass("cs-header-text displayFlexRow");
        if(!$(".cs-header-text .tab-right").length) {
        	$(".cs-header-text").wrapInner("");
        }
        if(!$(".cs-header-text .tab-left").length) {
        	$("").prependTo(".cs-header-text");
        }

        $(".social-media-tabs .tab-button").each(function() {
        	var thisTab = this;

            $(".tab-right:contains('Facebook')",thisTab).parent().parent().addClass("facebook");
            $(".tab-right:contains('Twitter')",thisTab).parent().parent().addClass("twitter");
            $(".tab-right:contains('Vimeo')",thisTab).parent().parent().addClass("vimeo");
            $(".tab-right:contains('Instagram')",thisTab).parent().parent().addClass("instagram");
            $(".tab-right:contains('Pinterest')",thisTab).parent().parent().addClass("pinterest");
            $(".tab-right:contains('Google')",thisTab).parent().parent().addClass("google");
            $(".tab-right:contains('Linked In')",thisTab).parent().parent().addClass("linkedin");
            $(".tab-right:contains('Flickr')",thisTab).parent().parent().addClass("flickr");
            $(".tab-right:contains('RSS')",thisTab).parent().parent().addClass("rssfeed");
        });

        $(".social-media-tabs").csAppAccordion({
            "accordionBreakpoints" : [960, 768, 640, 480, 320]
        });
    }

    function customMegaMenu() {
        var megaMenuData = [
            [
                ["/cms/lib/NY02208059/Centricity/Template/GlobalAssets/images///channel_images/MiddleSchoolTolerance.jpg", "About Us", "Learn about the Great Neck Public Schools, read our mission statement and educational philosophy, find out how to contact us, view our calendars, and discover the rich history of academic excellence and educational success that is the hallmark of our school district."]
            ],
            [
                ["/cms/lib/NY02208059/Centricity/Template/GlobalAssets/images///channel_images/SuperintendentAtLakeville.jpg", "Administration", "The Central Administration of the Great Neck Public Schools works collaboratively with students, staff, parents, and community members to establish a vision of educational excellence, hires qualified staff, and oversees the educational programs and services of the school district. "]
            ],
            [
                ["/cms/lib/NY02208059/Centricity/Template/GlobalAssets/images///channel_images/Bd-of-Ed-2018.jpg", "Board of Education", "The Great Neck Public Schools Board of Education consists of five residents elected to three-year terms in our annual May election. The Board has broad legal powers and responsibilities to set policy, supervise the administration, and levy property taxes to support school programs and district operations."]
            ],
