         // BUILD THE MEGA MENUS
         var i = 0;
         $("#sw-channel-list-container li.sw-channel-item").each(function() {
            // LOWERCASE THE CHANNEL NAME
            var channelText = $("> a span", this).text().toLowerCase();

            // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
            if(channelText == "home") {
              	$(this).addClass("home");
            } else if(channelText == "calendar") {
              	$(this).addClass("calendar");
            }

            if(channelText != "home" && channelText != "calendar" && $(this).hasClass("has-dropdown")) {
            	$(this).addClass("mega-channel");
            }

            //IGNORE HOME AND CALENDAR CHANNELS
            if(channelText != "home" && channelText != "calendar" && $(this).hasClass("has-dropdown")) {

                if(i <= megaMenuData.length - 1) {
                    // ADD MEGA CHANNEL CLASS
                    $(this).addClass("mega-channel");

                    // ADD STRUCTURE TO THE DOM
                    $(".sw-channel-dropdown", this).removeAttr("class").wrap('
');
                    $(".mega-column.two.sections",this).wrap('
');
                    $('.mega-column.two.sections',this).after('

');

                    // ADD CAPTION
                    if(jQuery.trim(megaMenuData[i][0][2]) != "") {
                      var captionColumn = '
' + jQuery.trim(megaMenuData[i][0][2]) + '';
                      $(".sw-channel-dropdown .mega-columns .mega-column.one.info .caption-cont", this).prepend(captionColumn);
                    }

                    // ADD TITLE
                    if(jQuery.trim(megaMenuData[i][0][1]) != "") {
                      var titleColumn = '
' + jQuery.trim(megaMenuData[i][0][1]) + '';
                      $(".sw-channel-dropdown .mega-columns .mega-column.one.info .caption-cont", this).prepend(titleColumn);
                    }

                    // ADD IMAGE
                    var imgColumn = '';
                    var imgSrc = jQuery.trim(megaMenuData[i][0][0]);
                    var srcSplit = imgSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                        imgColumn = '
';
                        $(".sw-channel-dropdown .mega-columns .mega-column.one.info", this).prepend(imgColumn);
                    }

                    i++;
                }
            }
         });

         $(".sw-channel-dropdown").each(function() {
         	var thisDropdown = this;
         	if($(".mega-column",thisDropdown).length == 1) {
               $("li.mega-columns",thisDropdown).css("width", "228px");
            }

            if(!$(".mega-channel-title",thisDropdown).length && !$(".mega-channel-caption",thisDropdown).length) {
            	$(".caption-cont",thisDropdown).remove();
            }

            if(!$(".channel-img-cont",thisDropdown).length && !$(".mega-channel-title",thisDropdown).length && !$(".mega-channel-caption",thisDropdown).length) {
            	$(".mega-column.one",thisDropdown).remove();
            }
         });
    }

