var docloc = String(document.location);
var newsletter_region = "longisland";
if(docloc.indexOf("longisland") > -1) { newsletter_region = "longisland"; }
if(docloc.indexOf("newjersey") > -1) { newsletter_region = "newjersey"; }
if(docloc.indexOf("westchester") > -1) { newsletter_region = "westchester"; }
if(docloc.indexOf("hudsonvalley") > -1) { newsletter_region = "hudsonvalley"; }
if(docloc.indexOf("bronx") > -1) { newsletter_region = "bronx"; }
if(docloc.indexOf("brooklyn") > -1) { newsletter_region = "brooklyn"; }
if(docloc.indexOf("connecticut") > -1) { newsletter_region = "connecticut"; }

$(document).ready(function() {

  $('#newsletter_submit').click( function(e){
     var email = $("#newsletter_email").val();
     if(email.indexOf("@") == -1) { alert("Please enter an email address"); return; }
     document.location="https://cb.sailthru.com/join/5up/signup?region="+newsletter_region+"&email="+email+"";
  });

});

section.script:
$(function(){
    if(window.simpleMenuALoaded) return;
    var $body = $('body');
    var $columns = $('#WNCols234-5')
    var wipe = function() {
        $body.removeClass(function (index, css) {
            return (css.match (/(^|\s)nav-\S+/g) || []).join(' ');
        });
    }
    $('nav.simple-a.main:first a').each(function() {
        var $$ = $(this)
        $$.on('hover',function() {
            wipe()
            $body.addClass('nav-feature-' + $$.data('id'))
        })
    });
    $columns.on('mouseenter',wipe);
    window.simpleMenuALoaded = true;
});
