/*! jQuery UI - v1.12.1 - 2018-12-19
 * http://jqueryui.com
 * Includes: unique-id.js
 * Copyright jQuery Foundation and other contributors; Licensed MIT */

(function (t) {
    "function" == typeof define && define.amd ? define(["jquery"], t) : t(jQuery);
})(function (t) {
    t.ui = t.ui || {}, t.ui.version = "1.12.1", t.fn.extend({
        uniqueId: function () {
            var t = 0;
            return function () {
                return this.each(function () {
                    this.id || (this.id = "ui-id-" + ++t);
                });
            };
        }(),
        removeUniqueId: function () {
            return this.each(function () {
                /^ui-id-\d+$/.test(this.id) && t(this).removeAttr("id");
            });
        }
    });
});


//////////Accordions//////////

if (document.getElementsByClassName('accordionblock')) {

    //Add h2 arrow, add tabindex, add role, add aria

    $("div.accordionblock h2").addClass("arrowdown");
    $("div.accordionblock h2").attr("tabindex", "0");
    $("div.accordionblock h2").attr("role", "button");
    $("div.accordionblock h2").attr("aria-expanded", "false");

    //Wrap everything under h2 with specific class
    $("div.accordionblock h2").each(function () {
        $(this).nextUntil("h2").wrapAll("
");
    });

    //Add hairline after last accordion
    $(".accordionblock div.accordiontoggle").append("");

    //Slide accordion function
    $("div.accordionblock h2").click(function () {
        $(this).next(".accordiontoggle").slideToggle("250");
    });

    $("div.accordionblock h2").toggle(
        function () {
            $(this).removeClass("arrowdown");
            $(this).addClass("arrowup");
            $(this).attr("aria-expanded", "true");
        },
        function () {
            $(this).removeClass("arrowup");
            $(this).addClass("arrowdown");
            $(this).attr("aria-expanded", "false");
        });

    //Give h2 an unique ID
    $("div.accordionblock h2").each(function () {
        $(this).uniqueId();
    });

    //Give div under h2 ID for aria label
    $("div.accordionblock h2").each(function () {
        let ariaId = $(this).attr("id");
        $(this).next(".accordiontoggle").attr("aria-labelledby", ariaId);
    });

    //Activate button on enter key press
    $(".arrowdown").keypress(function (e) {
        let key = e.which;
        if (key == 13) // the enter key code
        {
            $(this).click();
            return false;
        }
    });

}

//If css class "telLink" is found, turn xxx-xxx-xxxx phone format into a link

if (document.getElementsByClassName('telLink')) {

    $("div.telLink").find("*").each(function () {
        $(this).html($(this).html().replace(/([0-9]{3}|[0-9]{3}-)[0-9]{3}-[0-9]{4}/gi, "$&"));
    });

}

