//Documents in accordions

if (document.querySelector('.accordionDoc')) {

    document.querySelectorAll('.accordionDoc h2').forEach(function (el) {

        if (el.getAttribute("aria-expanded") === "false") {

            document.querySelectorAll('.translated-documents').forEach(function (e) {

                e.style.display = "none";

            });

        }

        el.addEventListener('click', function (ele) {
            let accordionDocId = document.querySelector('.accordionDoc > h2').id;
            document.querySelectorAll('.translated-documents').forEach(function (e) {
                if (e.style.display === "none") {
                    e.style.display = "block";
                    e.setAttribute("aria-labelledby", accordionDocId);
                    if (e.previousElementSibling.previousElementSibling.querySelector('hr')) {
                        e.previousElementSibling.previousElementSibling.querySelector('hr').style.display = "none";
                        e.previousElementSibling.previousElementSibling.style.paddingBottom = "0";
                    }
                } else {
                    e.style.display = "none";
                    e.previousElementSibling.previousElementSibling.style.paddingBottom = "2rem";
                }
            });

        });

        el.addEventListener('keypress', function (elem) {
            let key = elem.which || elem.keyCode;

            if (key === 13) {
                document.querySelectorAll('.translated-documents').forEach(function (e) {
                    if (e.style.display === "none") {
                        e.style.display = "block";
                        if (e.previousElementSibling.previousElementSibling.querySelector('hr')) {
                            e.previousElementSibling.previousElementSibling.querySelector('hr').style.display = "none";
                        }
                    } else {
                        e.style.display = "none";
                    }
                });
            }
        });

    });

}

//Accordion Anchor Link

//

function accordionAnchor() {

    if (document.querySelector("#pagedocuments")) {

        if (document.querySelector("#pagedocuments").getAttribute("aria-expanded") === "false") {

            document.querySelector("#pagedocuments").click();

            window.location.hash = 'pagedocuments';

        }

        if (document.querySelector("#pagedocuments").getAttribute("aria-expanded") === "true") {

            history.pushState("", document.title, window.location.pathname + window.location.search);
            window.location.hash = 'pagedocuments';

        }

    }

}

//onclick when Enter key pressed
if (document.querySelector('#clicking')) {
    document.querySelector('#clicking').addEventListener('keypress', function (elem) {
        let key = elem.which || elem.keyCode;

        if (key === 13) {
            document.querySelector('#clicking').click();
        }

    });
}

