///////Open external links in new window; add link-off icon///////
let hostURL = window.location.hostname;

//Prevent email links from opening in a new window, which can cause issues.
document.querySelectorAll('a').forEach(function (el) {
    if ((el.getAttribute('href') && el.getAttribute('href').includes('mailto')) || (el.getAttribute('href') && el.getAttribute('href').includes('schools.nyc.gov'))) {
        el.classList.add('noLinkIcon');
    }
});

if (hostURL.includes(".nyced.org") && !hostURL.includes("siteimprove.com")) {

    $('a').each(function () {
        let checkURL = new RegExp('/' + window.location.host + '/');
	let checkDoc = new RegExp('/' + window.location.host + '/docs/');
        if ((!checkURL.test(this.href) && !this.href.includes(".nyced.org/") && $(this).hasClass("noLinkIcon") == false) || (checkDoc.test(this.href) || this.href.includes(".nyced.org/docs/"))) {

            $(this).not(":has('svg')").append("  \(Open external link\)");

            $(this).click(function (event) {
                event.preventDefault();
                event.stopPropagation();
                window.open(this.href, '_blank');
            });
        }
    });

}

window.setInterval('refreshDiv()', 300);
function refreshDiv() {
	if (document.querySelectorAll('.event-item').length > 0) {
	document.querySelectorAll('.event-item').forEach(function(value, i) {
		var ifAllDayEventHomePage = document.querySelectorAll('.event-item')[i].getElementsByTagName('span')[0].innerText.includes('-');
		if (ifAllDayEventHomePage && !document.querySelectorAll('.event-item')[i].classList.contains("updatedDate")) {
			var getDateHtml = document.querySelectorAll('.event-item')[i].getElementsByTagName('span')[0].innerText.replace(/\r?\n|\r/g, '');
			var spliteDate = getDateHtml.split('-');
			var startDate = spliteDate[0];
			var d = new Date(spliteDate[1]);
			var todayDate = d;
			todayDate.setDate(todayDate.getDate() - 1);
			document.querySelectorAll('.event-item')[i].className += " updatedDate";
			document.querySelectorAll('.event-item')[i].getElementsByTagName('span')[0].innerHTML = startDate + ' - ' + formatDate(new Date());
			function formatDate(date) {
				var monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
				var weekDay = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
				var weekdayIndex = todayDate.getDay();
				var day = todayDate.getDate();
				var monthIndex = todayDate.getMonth();
				var year = todayDate.getFullYear();
				return (
					weekDay[weekdayIndex] + ', ' + monthNames[monthIndex] + ' ' + ('0' + day).slice(-2) + ', ' + year
				);
			}
		}
	});
	}
}
