input.script:
var scrolled = true;
$(document).ready(function () {
    $(window).scroll(function () {
        if (scrolled) {
            var top = $(window).scrollTop();
            var fullHeight = $("body > form").outerHeight();
            var winHeight = window.innerHeight;
            if (top + winHeight >= fullHeight - 20) {
                $.each($(".social_media_buttons > a"), function (i, v) {
                    setTimeout(function () {
                        $(v).addClass("MotionButton");
                    }, 100 * i);
                });
                scrolled = false;
            }
        }
    });
    if (scrolled) {
