function tabs(selectedtab) {    
    // contents
    var s_tab_content = "tab_content_" + selectedtab;   
    var contents = document.getElementsByTagName("div");
    for(var x=0; x<contents.length; x++) {
        name = contents[x].getAttribute("name");
        if (name == 'tab_content') {
            if (contents[x].id == s_tab_content) {
            contents[x].style.display = "block";                        
            } else {
            contents[x].style.display = "none";
            }
        }
    }   
    // tabs
    var s_tab = "tab_" + selectedtab;       
    var tabs = document.getElementsByTagName("a");
    for(var x=0; x<tabs.length; x++) {
        name = tabs[x].getAttribute("name");
        if (name == 'tab') {
            if (tabs[x].id == s_tab) {
            tabs[x].className = "active";                       
            } else {
            tabs[x].className = "";
            }
        }
    }     
}

var curWindowOnLoad = window.onload;
window.onload = function() {
	if(curWindowOnLoad) {
		curWindowOnLoad();
	}
	if(document.getElementsByClassName) {
		var newsImages = document.getElementsByClassName("news-img");
		for(i=0; i<newsImages.length; ++i) {
			newsImages[i].parentNode.style.marginRight = "1px";
		}
		var hpImages = document.getElementsByClassName("hp-img");
		for(i=0; i<hpImages.length; ++i) {
			hpImages[i].parentNode.style.marginRight = "1px";
		}
	}
};
