//SKELETON
	function pgScreenSize(){
		var getWidth = $(window).width();

		if (getWidth >= 1024) { //Desktop View
			multimediaRotator();
			sameHeightCols($('#hp-content > .hp.column'));
		}else{ //mobile bps
			if(getWidth >= 480){ //over 320
				multimediaRotator();
			}

			if(getWidth < 640){ //under 640
				appView();
			}
		}

		if( $('.hp').length > 0 ){ //if on the homepage
			//proceed to move and clone the logo elem, but only once.
			if( $('#gb-logo-cont-clone').length <= 0 ){
				var logoBoxClone = $('#gb-logo-cont').clone(false);
		       		logoBoxClone.attr('id', 'gb-logo-cont-clone');
		       		logoBoxClone.addClass('hide960 show768');
		       		$('#hp-slideshow').prepend(logoBoxClone);
		       	}
		       	if( $('#hp-slideshow .rs-gallery-left-cont #gb-logo-cont').length <= 0 ){
		       		$('#gb-logo-cont').addClass('hide768');
		       		$('#hp-slideshow .rs-gallery-left-cont').prepend($('#gb-logo-cont'));
			}
		}
	}

