	$(window).load(function() {
    	var vpw = $(window).width();
		calendarListView();
		searchText();
        $('#hp-video iframe').attr('tabindex', '-1');
        $('.cs-footer-link a span:contains("Site Map")').html('SITEMAP');
        if(vpw < 640){
            $('#ui-btn-calendarlist-list').click();
        } else {
        	$('#calendar-btn-change-month').click();
        }
	});

    function resize() {
    	var vpw = $(window).width(),
        	header = $('header').height();

        if(vpw < 1024) {
        	$('.gb.footer.one').addClass('cs-jfs').removeClass('cs-jsa');
            $('#cs-footer-links').prepend($('.gb.footer.one .footer-divider.first'));
            $('#cs-footer-links').prepend($('.gb.footer.one .cs-footer-download'));
            $('#cs-sw-footer').prepend($('#cs-footer-links'));
            $('.hp main').prepend($('.hp #cs-global-icons-outer'));
            $('.hp #hp-slideshow').prepend($('.hp .gb-school-info'));
            $('#cs-global-icons').addClass('cs-fcc cs-jc').removeClass('cs-fc cs-jsa');
            $('.hp.row.one').removeClass('cs-fcc').addClass('cs-fc');
            if($('#gb-page').hasClass('false')){
            	$('#gb-page').css('margin-top', header + 'px');
            }
            $('.gb-footer-address-inner').removeClass('cs-jfs').addClass('cs-jc');
            if(vpw < 768) {
                $('.hp #gb-header-outer').append($('.hp .gb-school-info')).append($('.hp #cs-global-icons-outer'));
                $('#cs-global-icons').removeClass('cs-fcc cs-jc').addClass('cs-fc cs-jsa');
                $('.hp.row.one').removeClass('cs-fc').addClass('cs-fcc');
                if($('#gb-page').hasClass('false')){
            		$('#gb-page').css('margin-top', header + 'px');
                }
                $('.gb-footer-address-inner').removeClass('cs-jfs').addClass('cs-jc');
                if(vpw < 640) {
                    if($('#gb-page').hasClass('false')){
                        $('#gb-page').css('margin-top', '35px');
                    }
                	$('.gb-footer-address-inner').addClass('cs-jfs').removeClass('cs-jc');

                }
            }
        } else {
        	$('.gb.footer.one').removeClass('cs-jfs').addClass('cs-jsa');
            $('.gb.footer.one').prepend($('#cs-footer-links .footer-divider'));
            $('.gb.footer.one').prepend($('#cs-footer-links .cs-footer-download'));
            $('#cs-sw-footer').append($('#cs-footer-links'));
            $('.hp main').prepend($('.hp #cs-global-icons-outer'));
            $('#hp-slideshow').prepend($('.gb-school-info'));
            $('#cs-global-icons').addClass('cs-fcc cs-jc').removeClass('cs-fc cs-jsa');
            $('.hp.row.one').removeClass('cs-fcc').addClass('cs-fc');
            if(!($('#gb-page').hasClass('true'))){
            	$('#gb-page').css('margin-top', header + 'px');
            }
            $('.gb-footer-address-inner').removeClass('cs-jfs').addClass('cs-jc');
        }
    }

	$(function(){

		$(window).bind("resize", function(){ 
			calendarListView();
            mobile();
    		resize();
		});

        // GLOBAL ICONS
        var iconNum = "4";

        if(iconNum == '4;3;2;1;0'){
        	iconNum = 4;
        }

        var eleIcon = '/cms/lib/NY01913963/Centricity/Template/12/imgs/ele-icon.png',
        	gbIcons = [
            	'/cms/lib07/NY01913963/Centricity/Template/GlobalAssets/icons/calendar1.png',
            	'/cms/lib07/NY01913963/Centricity/Template/GlobalAssets/icons/elephant.png',
            	'Social Media/22.png',
            	'/cms/lib07/NY01913963/Centricity/Template/GlobalAssets/icons/lunch.png'
                ];

        $("#cs-global-icons").creativeIcons({
            "iconNum"		: iconNum, // NUMBER OF ICONS
            "iconColor"		: "#FFF", // ICON COLOR
            "icon1"			: ["/cms/lib07/NY01913963/Centricity/Template/GlobalAssets/icons/calendar1.png", "Calendar", "/page/2", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            "icon2"			: ["/cms/lib07/NY01913963/Centricity/Template/GlobalAssets/icons/elephant.png", "Family Portal", "https://campus.somersschools.org/campus/portal/somers.jsp ", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            "icon3"			: ["Social Media/22.png", "Connect", "https://www.facebook.com/scsd.tuskers", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            "icon4"			: ["/cms/lib07/NY01913963/Centricity/Template/GlobalAssets/icons/lunch.png", "Meals", "http://somersschools.nutrislice.com/", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            "siteID"		: "4",
            "siteAlias"		: "somers",
            "calendarLink"	: "/Page/2",
            "contactEmail"	: "help@somersschools.org",
            "onComplete"	: function(){
            	$('.global-icon').addClass('cs-fc cs-aic');
                if($('.sp').length) {
                	$('.global-icon').hover(function(){
                    	var content = $('.sp.column.one').offset();
                        var shift = 175 - content.left;

                        if(shift > 0) {
                        	$('.sp.column.two').stop(true, true).animate({
                            	"padding-right" : shift + 'px'
                            }, 300);
                        }
                    }, function(){
                    	$('.sp.column.two').stop(true, true).animate({
                            "padding-right" : '0px'
                        }, 300);
                    });
                }
            }
        });

        if(!true) {
        	$('.cs-mystart-button.anonymous-alerts').remove();
        }

        var additionalHeaders = "", additionalLinks = "";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
				["Calendar", "/page/2"],
				["Family Portal", "https://campus.somersschools.org/campus/portal/somers.jsp "],
				["Connect", "https://www.facebook.com/scsd.tuskers"],
				["Meals", "http://somersschools.nutrislice.com/"]
			];

			for(var i = 0; i < iconNum; i++) {
				var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
				additionalLinks += "1, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
			}

			if(iconNum > 0) {
				additionalHeaders = "Global Icons";
			}


		// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"		: 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"      : "#1A1A1A", // DEFAULT - #1A1A1A
            "secondaryColor"    : "#0C0C0C", // DEFAULT - #0C0C0C
            "accentColor"       : "#BABABA", // DEFAULT - #BABABA
