	function mediaRotator(){

        var loadScript = function(src, callbackfn) {
            var newScript = document.createElement("script");
            newScript.type = "text/javascript";
            newScript.setAttribute("async", "true");
            newScript.setAttribute("src", src);

            if(newScript.readyState) {
                newScript.onreadystatechange = function() {
                    if(/loaded|complete/.test(newScript.readyState)) callbackfn();
                }
            } else {
                newScript.addEventListener("load", callbackfn, false);
            }

            document.documentElement.firstChild.appendChild(newScript);
        }

        //-- LOAD SCRIPT IF LOADED RUN MMR
		loadScript("//extend.schoolwires.com/creative/scripts/creative/rotate/cs.rs.multimedia.rotator.v2.min.js", function() { 
        	if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth" 			: 1500,
		        	"imageHeight" 			: 743,
		        	"appHeader" 			: "no",
		        	"titles" 			: "yes",
		        	"captions" 			: "no", //changed from yes by Lyndsay - Case 00534901 - 5.4.17
		        	"galleryOverlay" 		: "no",
		       		"linkedElement" 		: ["image"], // ["image", "title", "overlay", "all"]
		        	"playPause" 			: "no",
		        	"mainControls" 			: "yes",
		        	"contMoreLink" 			: "no",
		        	"contMoreLinkText" 		: "LEARN MORE",
		        	"descMoreLink" 			: "yes",
		        	"descMoreLinkText" 		: "LEARN MORE",
		        	"bullets" 			: "no",
		        	"bulletControls" 		: "no",
		        	"thumbnails" 			: "no",
		        	"thumbnailWidth" 		: 122,
		        	"thumbnailHeight" 		: 53,
		        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
		        	"hoverPause" 			: "yes",
		        	"fullScreenRotator" 		: "yes", // image dimensions work well at 1500x915
		        	"fullScreenBreakpoints"   	: ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		        	"fullScreenVideo" 		: "no",
		        	"transitionType" 		: "fade", // fade or slide
		        	"transitionSpeed" 		: 3,
		        	"transitionDelay" 		: 8,
					"autoRotate" 			: "yes",
		        	"onImageLoad" 			: function() { },
		        	"allImagesLoaded" 		: function() { },
		        	"onThumbnailLoad" 		: function() { },
		        	"allThumbnailsLoaded" 		: function() { },
		        	"onTransitionStart" 		: function() { },
		        	"onTransitionEnd" 		: function() { 
                    	setTimeout(function(){
                            if($('.rs-title.active').hasClass('hidden')) {
                                $('.rs-gallery-desc').fadeOut();
                            } else {
                                $('.rs-gallery-desc').fadeIn();
                            }
                        }, 200 );
                    },
		        	"watchVidClick" 		: function() { },
		        	"closeVidClick" 		: function() { },
		        	"allLoaded" 			: function() { 
                    	if($('.rs-title.active').hasClass('hidden')) {
                        	$('.rs-gallery-desc').fadeOut();
                        } else {
                        	$('.rs-gallery-desc').fadeIn();
                        }
                    	$('.rs-gallery-desc, .rs-gallery-control.back, .rs-gallery-control.next').wrapAll('

');
                        $('.rs-gallery-control.back, .rs-gallery-control.next').html(arrowSVG);
                    }
				});
			}
         });
	}

    function videoContent(){
    	var	vidTitle = '',
        	vidCaption = '',
            vidLink = '';
        if(vidTitle.replace(' ', '') != '' && vidCaption.replace(' ', '') != '' && vidLink.replace(' ', '') != '')
          var videoHTML = '
';
        if(vidTitle.replace(' ', '') != '') {
            videoHTML += '
' + vidTitle + '';
        }
        if(vidCaption.replace(' ', '') != '') {
            videoHTML += '
' + vidCaption + '';
        }
        if(vidLink.replace(' ', '') != '') {
            videoHTML += '
LEARN MORE ';
        }


        if(videoHTML != undefined) {
        	videoHTML += '';
        	$('#hp-video-content').prepend(videoHTML);	
        }
    }

    function mobile(){
    	var vpw = $(window).width();
        if(vpw < 768){
            $('.hp.row.one .hp.column.two .ui-widget-header').unbind('click').click(function(){
            	var detail = $(this).next();
                if($(this).hasClass('open')){
                    $(this).removeClass('open');
                    detail.slideUp();
                } else {
                    $(this).addClass('open');
                    detail.slideDown();
                }
            });
        }
    }

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Families");
		}
    }

