div.script:
var googleTranslateElementInit, translateWindowWidth;
googleTranslateElementInit = function(element) {
  new google.translate.TranslateElement({
    pageLanguage: "en",
    layout: google.translate.TranslateElement.InlineLayout.SIMPLE
  }, element);
};

translateWindowWidth = function() {
  if ($(window).width() > 768) {
    googleTranslateElementInit("google_translate_element");
  } else {
    googleTranslateElementInit("google_translate_element--mobile");
  }
};

jQuery(document).ready(function($){

    if( Cookies.get('swnza_hide_banner_cookie') != undefined ) {
        $('.swnza_banner').hide();
    }

    $('#swnza_close_button_link').click(function(){
      Cookies.set('swnza_hide_banner_cookie', 1, { expires: 1, path: '/' }); //expire the cookie after 24 hours.

      $('.swnza_banner').hide();
    });
});

