function drawIconThree(ctx) {
	ctx.save();
	ctx.beginPath();
	ctx.moveTo(11.7, 4.8);
	ctx.bezierCurveTo(10.2, 6.3, 10.5, 8.2, 10.0, 10.1);
	ctx.lineTo(1.7, 1.7);
	ctx.bezierCurveTo(1.5, 1.5, 1.3, 1.4, 1.0, 1.4);
	ctx.bezierCurveTo(0.4, 1.4, 0.0, 1.9, 0.0, 2.4);
	ctx.lineTo(0.0, 2.8);
	ctx.bezierCurveTo(0.0, 5.8, 1.3, 11.4, 5.4, 15.8);
	ctx.lineTo(0.6, 20.6);
	ctx.bezierCurveTo(-0.2, 21.4, -0.2, 22.6, 0.6, 23.4);
	ctx.bezierCurveTo(1.4, 24.2, 2.6, 24.2, 3.4, 23.4);
	ctx.lineTo(8.2, 18.7);
	ctx.lineTo(11.0, 21.5);
	ctx.lineTo(13.5, 19.3);
	ctx.lineTo(17.6, 23.4);
	ctx.bezierCurveTo(18.4, 24.2, 19.6, 24.2, 20.4, 23.4);
	ctx.bezierCurveTo(21.2, 22.6, 21.2, 21.4, 20.4, 20.6);
	ctx.lineTo(13.8, 14.0);
	ctx.bezierCurveTo(15.7, 13.5, 17.7, 13.8, 19.2, 12.3);
	ctx.lineTo(23.6, 7.9);
	ctx.bezierCurveTo(24.1, 7.4, 24.1, 6.6, 23.6, 6.1);
	ctx.bezierCurveTo(23.1, 5.6, 22.3, 5.6, 21.8, 6.1);
	ctx.lineTo(17.9, 10.0);
	ctx.bezierCurveTo(17.6, 10.3, 17.1, 10.3, 16.8, 10.0);
	ctx.bezierCurveTo(16.5, 9.7, 16.5, 9.2, 16.8, 8.9);
	ctx.lineTo(20.8, 5.0);
	ctx.bezierCurveTo(21.3, 4.5, 21.3, 3.7, 20.8, 3.2);
	ctx.bezierCurveTo(20.3, 2.7, 19.5, 2.7, 19.0, 3.2);
	ctx.lineTo(15.1, 7.2);
	ctx.bezierCurveTo(14.8, 7.5, 14.3, 7.5, 14.0, 7.2);
	ctx.bezierCurveTo(13.7, 6.9, 13.7, 6.4, 14.0, 6.1);
	ctx.lineTo(17.9, 2.2);
	ctx.bezierCurveTo(18.4, 1.7, 18.4, 0.9, 17.9, 0.4);
	ctx.bezierCurveTo(17.4, -0.1, 16.6, -0.1, 16.1, 0.4);
	ctx.lineTo(11.7, 4.8);
	ctx.closePath();
