	function checkForDevice(){
		var agent = navigator.userAgent.toLowerCase();
		var scrWidth = screen.width;
		var scrHeight = screen.height;
		// The document.documentElement dimensions seem to be identical to
		// the screen dimensions on all the mobile browsers I have tested so far
		var elemWidth = document.documentElement.clientWidth;
		var elemHeight = document.documentElement.clientHeight;
		// We need to eliminate Symbian, Series 60, Windows Mobile and Blackberry
		// browsers for this quick and dirty check. This can be done with the user agent.
		var otherBrowser = (agent.indexOf("series60") != -1) || (agent.indexOf("symbian") != -1) || (agent.indexOf("windows ce") != -1) || (agent.indexOf("blackberry") != -1);
		// If the screen orientation is defined we are in a modern mobile OS
		var mobileOS = typeof orientation != 'undefined' ? true : false;
		// If touch events are defined we are in a modern touch screen OS
		var touchOS = ('ontouchstart' in document.documentElement) ? true : false;
		// iPhone and iPad can be reliably identified with the navigator.platform
		// string, which is currently only available on these devices.
		var iOS = (navigator.platform.indexOf("iPhone") != -1) ||
		        (navigator.platform.indexOf("iPad") != -1) ? true : false;
		// If the user agent string contains android then it is Android. If it
		// does not but it is not another browser, not an iOS device and we are in
		// a mobile and touch OS then we can be 99% certain that it is Android.
		var android = (agent.indexOf("android") != -1) || (!iOS && !otherBrowser && touchOS && mobileOS) ? true : false;
		if(android == true || iOS == true || mobileOS == true)
		{
			$(".sw-mystart-dropdown .sw-dropdown-list").css("max-height", "none");
		}; 
	}
	function dynamicStylesheet(){
		var dynStyleSheet = document.createElement('style');
		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];
			if(head) {
	      			head.appendChild(dynStyleSheet);
			}
			var dynStyles = "a, a:visited {" +
					"	color: " + linkColor + ";" +
					"}" +
					"li.sw-channel-item > a {" +
					"	color: " + channelTextColor + ";" +
					"}" +
					"li.sw-channel-item.active > a, li.sw-channel-item:hover > a {" +
					"	color: " + channelTextActiveColor + ";" +
					"}" +
					"ul.sw-channel-dropdown {" +
					"	background-color: " + channelBarColor + ";" +
					"	border-color: " + bodyColor + ";" +
					"}" +
					"ul.sw-channel-dropdown li a {" +
					"	color: " + channelTextColor + ";" +
					"}" +
					"ul.sw-channel-dropdown li a:hover {" +
					"	color: " + channelTextActiveColor + ";" +
					"}";
			var rules = document.createTextNode(dynStyles);
			if(dynStyleSheet.styleSheet){// IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}
