	function tabCustomization() {
		//DONT ALLOW MORE THAN 2 APPS
		$("#sw-content-container2 div.tab-button").each(function(){
			$(this).attr("id", $(this).index());
			var tabCount = $(this).attr("id");
			if (tabCount > 1){
				$(this).hide();
			}
		});

		//TABS BEGIN
		var tabButton = $("div.tabs-container div.tab-button");

		//SET INITIAL Z-INDEX ON TABS AND FIRST TAB HEADER COLOR
		resetTabs();
		$(tabButton).eq(0).css("color", tabTextColor);

		//RESET ALL TAB Z-INDEXS WHEN ONE IS CLICKED
		function resetTabs(){
			$(tabButton).eq(0).css("z-index", "6005").css("color" , tabTextColor);
			$(tabButton).eq(1).css("z-index", "6004").css("color" , tabTextColor);
			$(tabButton).eq(2).css("z-index", "6003").css("color" , tabTextColor);
			$(tabButton).eq(3).css("z-index", "6002").css("color" , tabTextColor);
		}

		//PUT CURRENT TAB ON TOP WITH HEADER COLOR
		$(tabButton).click(function() {
			resetTabs();
			$(this).css("z-index", "6006").css("color" , tabActiveTextColor);
	   	});

	   	//TAB HEADER COLOR
	   	$("div.tab-button").css("color" , tabTextColor);
	   	$("div.tab-button:eq(0)").css("background" , "transparent url('/cms/lib/NC01001905/Centricity/Template/9/tab-divider.png') no-repeat scroll right 5px");
	   	$("div.tab-button:eq(1)").css("padding" , "15px 0px 0px 0px");
		$("div.tab-button.active").css("color", tabActiveTextColor);
		$("div.tab-button").hover(function() {
			$(this).css("color" , tabActiveTextColor);
		},
		function() {
			$("div.tab-button").not($("div.tab-button.active")).css("color" , tabTextColor);
		});

	   	//TABS END

		//CONTENT BACKGROUND ELEMENTS
		$("div#sw-content-container2").children().not("div.tabs-container").wrapAll("
");
	}

