window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'UA-155389907-2');

$(document).ready(function(){
    $("table").find("table[cellpadding]").each(function(i){
        var cellPad = $(this).attr("cellpadding");
        $(this).find("td,th").css({
            "padding" : cellPad
        });
    });
    $("table").find("table[cellspacing]").each(function(i){
        var cellSpace = $(this).attr("cellspacing");
        $(this).css({
            "border-spacing" : cellSpace+"px",
        });
    });

    $("table").find("table[border]").each(function(i){
        var border = $(this).attr("border");
        $(this).css({
            "border" : border+"px solid #ccc",
            "border-collapse" : "separate"
        });
    });
});

link.script:
//This scipt for Bootstrap 3 sub-menu dropdown on mobile view//
(function($){
    $(document).ready(function(){
        $('ul.dropdown-menu [data-toggle=dropdown]').on('click', function(event) {
        event.preventDefault(); 
        event.stopPropagation(); 
        $(this).parent().siblings().removeClass('open');
        $(this).parent().toggleClass('open');
        });
      });
 })(jQuery);

div.script:
	    	var step = 1;
		 	var rows = 2;
		 	var timer;
            var pause;

		 	function slideit()
		 	{
		 		if (step==1)
		 		{ 			
		 			$("#slide" + step).show("slow");
		 			if (rows>2){
		 				$("#slide" + (rows-1)).hide("slow");
		 				// $("div.social").removeClass("active");
		 			} 
		 		}else{ 					
			 		$("#slide" + step).show("slow");
			 		$("#slide" + (step-1)).hide("slow");
			 		// $("div.social").removeClass("active");	 		
		 		}
		 		if (step < (rows-1)) {
		 			step++;
		 		}else step = 1;

		 		timer=setTimeout("slideit()",10000);
		 	}
			slideit();


		 	var stepd = 1;
		 	var rowsd = 1;
		 	var timerd;
            var paused;
		 	function slideitd()
		 	{ 		
		 		if (stepd==1)
		 		{ 			
		 			$("#slided" + stepd).show("slow");
		 			if (rowsd>2) $("#slided" + (rowsd-1)).hide("slow");
		 			// $("div.social").removeClass("active");
		 		}else{ 					
			 		$("#slided" + stepd).show("slow");
			 		$("#slided" + (stepd-1)).hide("slow");
			 		// $("div.social").removeClass("active");	 		
		 		}
		 		if (stepd < (rowsd-1)) {
		 			stepd++;
		 		}else stepd = 1;

		 		timerd=setTimeout("slideitd()",10000);
		 	}
		 	slideitd();

		 	function stop(){
                clearTimeout(timer);
                pause=1;
            }

            function stop_d(){
                clearTimeout(timerd);
                paused=1;
            }

