    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ChannelBarIsSticky": false,
        "PasskeysAreMoved": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.DynStyles();
            this.Header();
            this.SchoolList();
            this.ChannelBar();
            this.StickyChannelBar();
            this.MovePassKeys();
            this.Body();
            this.ModEvents();
            this.AppScroller();
            this.Footer();
            this.Slideshow();
            this.AppAccordion();
            this.Search();
            this.GlobalIcons();
            this.SocialIcons();
            this.SlideshowResize();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){ _this.WindowScroll(); });

			csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
        	this.SlideshowResize();
            this.CalendarListView();

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.MovePassKeys();
            this.ChannelBar();
            this.StickyChannelBar();
            this.CalendarListView();
        },

        "WindowScroll": function() {
            this.StickyChannelBar();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "DynStyles": function() {
            var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var spBgColor = this.HexToRgb('#000000');
                var dynStyles = '#sp-content-outer, #spn-content-outer {' +
                					'background: linear-gradient(to top, rgba(' + spBgColor + ', 0) 0px, rgba(' + spBgColor + ', 0) 140px, rgba(' + spBgColor + ', .70) 40%, rgba(' + spBgColor + ', .70) calc(100% - 455px), rgba(255, 255, 255, .90) 100%), ' + 
                                    			'linear-gradient(to top, rgba(255, 255, 255, 1) 0px, rgba(255, 255, 255, 1) 95px, rgba(255, 247, 232, .80) 140px, rgba(255, 247, 232, 0) 40%);' +

                                '}';


                var rules = document.createTextNode(dynStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = dynStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },

        "HexToRgb": function(h) {
            var r = parseInt((this.CutHex(h)).substring(0,2),16);
            var g = parseInt((this.CutHex(h)).substring(2,4),16);
            var b = parseInt((this.CutHex(h)).substring(4,6),16);
            return r + ', ' + g + ', ' + b;
        },

        "CutHex": function(h) {
        	return (h.charAt(0)=="#") ? h.substring(1,7):h;
        },

