        "ChannelBar": function() {
        	if(this.GetBreakPoint() == "desktop") {
            	$("#gb-channel-list #channel-navigation li.sw-channel-item").each(function() {
                	if($(this).position().top == 0) {
                    	$(this).attr("data-tier", "one");
                    } else if($(this).position().top == 47) {
                    	$(this).attr("data-tier", "two");
                    } else if($(this).position().top == 94) {
                    	$(this).attr("data-tier", "three");
                    } else if($(this).position().top == 141) {
                    	$(this).attr("data-tier", "four");
                    }
                });
            }
        },

        "StickyChannelBar": function() {
            if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var gbNavOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= gbNavOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav").removeClass("sticky");
                                $("nav").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("nav").removeClass("sticky");
                            $("nav").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }

                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

