');
            $(".more-link").each(function() {
            	if($(this).parent().is("li")) {
                	$(this).unwrap();
                }
            	$("+ .more-link-under", this).remove();
            	$("span", this).text("View All");
                if($(this).closest(".ui-widget.app").find(".ui-widget-header-controls").length) {
                	$(this).closest(".ui-widget.app").find(".ui-widget-header").removeClass("ui-helper-hidden").find(".ui-widget-header-controls").append($(this));
                } else {
                	$(this).closest(".ui-widget.app").find(".ui-widget-header").removeClass("ui-helper-hidden").append($(this));
                }
            });

            // ADJUST CALENDAR LINKS
            $(".view-calendar-link").each(function() {
            	$(this).closest(".ui-widget.app").find(".ui-widget-header").removeClass("ui-helper-hidden").find(".ui-widget-header-controls").append($(this));
            });

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            // BUILD GOOGLE MAP URL
			var mapURL = encodeURIComponent("3600 Trent Road New Bern NC 28562");
			$("#gb-footer-map-link").attr("href", "//www.google.com/maps/place/" + mapURL);

            // SCROLL TO TOP BUTTON
            $(document).on("click keydown", "#gb-back-to-top", function(e){
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                    	if(template.GetBreakPoint() == "desktop") {
                        	$("#gb-channel-list #channel-navigation li:first-child > a").focus();
                        } else {
                        	$("#rs-menu-btn").focus();
                        }
                    });
                }
            });

            // CHECK APPS FOR LI'S
            $(".ui-widget.app").each(function() {
            	if(!$(".ui-widget-detail > ul > li", this).length) {
                	$(this).addClass("no-li-structure");
                }
            });
        },

        "ModEvents": function() {
            $(".upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "yes"
            });

            $(".upcomingevents .ui-article").each(function(){
                if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                    var moveArticle = $(this).html();
                    $(this).parent().prev().children().children().next().append(moveArticle);
                    $(this).parent().remove();
                } else {
                	$(".upcoming-column.right", this).prepend($(".joel-day", this));
                }
            });
        },

        "AppScroller": function() {
        	var template = this;

            function HpSlider(app) {
            	var _this = this;

                // SET VARIABLES
                this.app = app;
                this.appId = $(this.app).attr("id").replace("pmi-", "");
                this.slideNum = $(".ui-articles li", this.app).length;
                this.breakpoints = ["desktop", "768", "640", "480", "320"];
                this.headlineSlideMax = [4, 2, 2, 1, 1];
                this.eventsSlideMax = [5, 3, 2, 1, 1];
                this.animationDelay = 500;
                this.WindowWidth = $(window).width();

                // ADJUST STRUCTURE
                $(".ui-articles", this.app).attr("id", "hp-slide-view-" + this.appId);
                $(".ui-widget-detail", this.app).wrap('
');
                $(".ui-widget-detail-outer", this.app)
                    .prepend('')
                    .append('');
                $(".ui-widget-header-controls", this.app).prepend('' +
                	'' +
                	'' +
                '');

                // SET ATTRIBUTES AND ALIGNMENT
                this.CheckSlider();

                // BIND EVENTS
                this.Events();

                // RESET ATTRIBUTES AND ALIGNMENT ON WINDOW RESIZE
                $(window).resize(function() {
                	// SCROLLING ON iOS TRIGGERS THE WINDOW RESIZE EVENT SO WE WILL MAKE SURE THE WINDOW HAS ACTUALLY CHANGED BEFORE TRIGGERING
                	if($(window).width() != _this.WindowWidth) {
                		_this.CheckSlider();

                        _this.WindowWidth = $(window).width();
                    }
                });
            }

            HpSlider.prototype.Events = function() {
            	var _this = this;

                $(this.app).on("click keydown", ".hp-slider-btn", function(e) {
                    if(template.AllyClick(e)) {
                        e.preventDefault();

                        if(!$(".ui-widget.app", _this.app).hasClass("animating")) {
                            // SET VARIABLES
                            var clickedBtn = this;
                            var breakpointIndex = _this.breakpoints.indexOf(template.GetBreakPoint());
                            var slideWidth = $(".ui-articles > li[aria-hidden='false']", _this.app).outerWidth(true);
                            var slideMax = 0;

                            if($(".ui-widget.app", _this.app).hasClass("headlines")) {
                                slideMax = _this.headlineSlideMax[breakpointIndex];
                            } else if($(".ui-widget.app", _this.app).hasClass("upcomingevents")) {
                                slideMax = _this.eventsSlideMax[breakpointIndex];
                            }

                            // DISABLE THE BUTTONS TEMPORARILY
                            $(".ui-widget.app", _this.app).addClass("animating");

                            // BACK BUTTON WAS CLICKED
                            if($(this).hasClass("back")) {
                                // FIRST CHILD IS VISIBLE
                                if($(".ui-articles > li:first-child", _this.app).attr("aria-hidden") == "false") {
                                    // ANIMATE TO LAST CHILD
                                    $(".ui-articles > li", _this.app).attr({"aria-hidden": "false", "data-visibility": "visible"});
                                    $(".ui-articles", _this.app).css("left", "-" + ((_this.slideNum - slideMax) * slideWidth) + "px");

                                    // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                                    setTimeout(function() {
                                        $(".ui-articles > li:nth-child(-n+" + (_this.slideNum - slideMax) + ")", _this.app).attr({"aria-hidden": "true", "data-visibility": "hidden"});

                                        // ENABLE THE BUTTONS
                                        $(".ui-widget.app", _this.app).removeClass("animating");
                                    }, _this.animationDelay);
                                }

                                // ONE SLIDE BACKWARD
                                else {
                                    $(".ui-articles > li[data-visibility='visible']", _this.app).first().prev("li").attr({"aria-hidden": "false", "data-visibility": "visible"});
                                    $(".ui-articles", _this.app).css("left", (Number($(".ui-articles", _this.app).css("left").replace("px", "")) + slideWidth).toFixed(5) + "px");

                                    // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                                    setTimeout(function() {
                                        $(".ui-articles > li[data-visibility='visible']", _this.app).last().attr({"aria-hidden": "true", "data-visibility": "hidden"});

                                        // ENABLE THE BUTTONS
                                        $(".ui-widget.app", _this.app).removeClass("animating");
                                    }, _this.animationDelay);
                                }
                            }

                            // NEXT BUTTON WAS CLICKED
                            else {
                                // LAST CHILD IS VISIBLE
                                if($(".ui-articles > li:last-child", _this.app).attr("aria-hidden") == "false") {
                                    // ANIMATE TO FIRST CHILD
                                    $(".ui-articles > li", _this.app).attr({"aria-hidden": "false", "data-visibility": "visible"});
                                    $(".ui-articles", _this.app).css("left", "0px");

                                    // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                                    setTimeout(function() {
                                        $(".ui-articles > li:nth-child(n+" + (slideMax + 1) + ")", _this.app).attr({"aria-hidden": "true", "data-visibility": "hidden"});

                                        // ENABLE THE BUTTONS
                                        $(".ui-widget.app", _this.app).removeClass("animating");
                                    }, _this.animationDelay);
                                }

                                // ONE SLIDE FORWARD
                                else {
                                    $(".ui-articles > li[data-visibility='visible']", _this.app).last().next("li").attr({"aria-hidden": "false", "data-visibility": "visible"});
                                    $(".ui-articles", _this.app).css("left", (Number($(".ui-articles", _this.app).css("left").replace("px", "")) - slideWidth).toFixed(5) + "px");

                                    // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                                    setTimeout(function() {
                                        $(".ui-articles > li[data-visibility='visible']", _this.app).first().attr({"aria-hidden": "true", "data-visibility": "hidden"});

                                        // ENABLE THE BUTTONS
                                        $(".ui-widget.app", _this.app).removeClass("animating");
                                    }, _this.animationDelay);
                                }
                            }
                        }
                    }
                });
            }

            HpSlider.prototype.CheckSlider = function() {
            	var _this = this;
                var breakpointIndex = this.breakpoints.indexOf(template.GetBreakPoint());
                var slideMax = 0;

                // DISABLE THE BUTTONS TEMPORARILY
                $(".ui-widget.app", _this.app).addClass("animating");

                $(".ui-articles > li", this.app).attr({"aria-hidden": "false", "data-visibility": "visible"});  
                $(".ui-articles", this.app).css("left", "0px");

                if($(".ui-widget.app", this.app).hasClass("headlines")) {
                    slideMax = this.headlineSlideMax[breakpointIndex];

                    // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                    setTimeout(function() {
                    	$(".ui-articles > li", _this.app).attr({"aria-hidden": "true", "data-visibility": "hidden"});
                    	$(".ui-articles > li:nth-child(-n+" + _this.headlineSlideMax[breakpointIndex] + ")", _this.app).attr({"aria-hidden": "false", "data-visibility": "visible"});

                		// ENABLE THE BUTTONS
                        $(".ui-widget.app", _this.app).removeClass("animating");
                    }, _this.animationDelay);
                } else if($(".ui-widget.app", this.app).hasClass("upcomingevents")) {
                    slideMax = this.eventsSlideMax[breakpointIndex];

                    // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                    setTimeout(function() {
                    	$(".ui-articles > li", _this.app).attr({"aria-hidden": "true", "data-visibility": "hidden"});
                    	$(".ui-articles > li:nth-child(-n+" + _this.eventsSlideMax[breakpointIndex] + ")", _this.app).attr({"aria-hidden": "false", "data-visibility": "visible"});

                    	// ENABLE THE BUTTONS
                        $(".ui-widget.app", _this.app).removeClass("animating");
                    }, _this.animationDelay);
                }

                if(this.slideNum > slideMax) {
                    $(".hp-slider-btn", this.app).removeClass("hidden");
                } else {
                    $(".hp-slider-btn", this.app).addClass("hidden");
                }
            }

            // INITIALIZE SLIDERS IN THIS WAY TO MAKE THEM UNIQUE
            var sliders = {}, sliderIndex = 0;
            $("#sw-content-container2.ui-hp div.ui-widget.app").each(function() {
            	if($(this).hasClass("headlines") || $(this).hasClass("upcomingevents")) {
                	sliders[sliderIndex] = new HpSlider($(this).closest('div[id*="pmi-"]'));

                    sliderIndex++;
                }
            });
        },

