        "SlideshowResize": function() {
        	if($(".hp").length && $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").length) {
                var element = $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator");

                if(this.GetBreakPoint() == "desktop") {
                    var topOffSet = $(element).offset().top;
                    var screenWidth = $(window).width();
                    var screenHeight = $(window).height();
                    var screenRatio = screenHeight / screenWidth;
                    var containerHeight = screenWidth * screenRatio;
                    var offsetHeight = $(window).height() - topOffSet;
                    var offsetRatio = offsetHeight / screenWidth;
                    var galleryHeight = screenWidth * offsetRatio;
                    var imgWidth = screenWidth, imgHeight = 0;

                    if($(".rs-gallery-videos").length) {
                        var vidWidth = $(".rs-gallery-videos .video", element).width();
                        var vidHeight = $(".rs-gallery-videos .video", element).height() - 27;
                    }

                    // GET IMAGE RATIO
                    var initW = 1500, initH = 915;
                    var ratio = initH / initW;
                    var imgHeight = imgWidth * ratio;

                    if(imgHeight < screenHeight){
                        imgHeight = screenHeight;
                        imgWidth = imgHeight / ratio;
                    }

                    var centerWidth = (imgWidth - screenWidth) / 2;
                    var centerHeight = (imgHeight - screenHeight) / 2;

                    // APPLY THE DIMENSIONS
                    $(".rs-gallery-container", element).css("height", galleryHeight);
                    $(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li img", element).css({"width": imgWidth + "px", "height": imgHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
                } else {
                    // GET THE WIDTHS AND HEIGHTS WE NEED
                    var imgWidth = $(".rs-gallery-images ul.rs-gallery-images-list li", element).width();

                    if($(".rs-gallery-videos").length) {
                        var vidWidth = $(".rs-gallery-videos .video", element).width();
                        var vidHeight = $(".rs-gallery-videos .video", element).height() - 27;
                    }

                    // GET IMAGE RATIO
                    var initW = 1500, initH = 915;
                    var ratio = initH / initW;
                    var imgHeight = imgWidth * ratio;

                    // APPLY THE DIMENSIONS
                    $(".rs-gallery-container", element).css("height", imgHeight);
                    $(".rs-gallery-container .rs-gallery-images", element).css("height", imgHeight);
                    $(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list", element).css("height", imgHeight);
                    $(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li", element).css("height", imgHeight);
                    $(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li img", element).removeAttr("style");
                }

                // VIDEO RESIZE
                if($(".rs-gallery-videos").length) {
                    var moduleID = $(element).parent().attr("id").replace("module-content-", "");

                    $(".rs-gallery-videos .video iframe", element).css({"width": "auto", "height": "auto"});
                    var screenWidth = $(window).width();
                    var screenHeight = $(window).height();
                    var initVidW = 800, initVidH = 600;
                    var vidRatio = initVidH / initVidW;
                    var vidWidth = $("#rs-gallery-videos-" + moduleID + " .video").width();
                    var vidHeight = vidWidth * vidRatio;

                    if((vidHeight + 100) > screenHeight){
                        vidHeight = screenHeight - 140;
                    }

                    $("#rs-gallery-videos-" + moduleID + " .video").css({"height": (vidHeight + 40)});
                    $("#rs-gallery-videos-" + moduleID + " .video iframe").css({"width": vidWidth, "height": vidHeight});
                }
            }
        },

