var i = 0;
setInterval(function () {
    i++;
    if (i < 50) {
        if (colorValues($("header").css("background-color")) > 0.5) {
            $("header").addClass("Darken");
        } else {
            $("header").removeClass("Darken");
        }
        if (colorValues($("footer").css("background-color")) > 0.5) {
            $("footer").addClass("Darken");
            $(".SubFooter").addClass("Darken");
        } else {
            $("footer").removeClass("Darken");
            $(".SubFooter").removeClass("Darken");
        }
        if (colorValues($(".DynamicNav > ul > li:first-child a").css("background-color")) > 0.5) {
            $(".DynamicNav > ul > li:first-child a").addClass("Darken");
        } else {
            $(".DynamicNav > ul > li:first-child a").removeClass("Darken");
        }
        if (colorValues($(".DynamicNav_currentpage_link").css("background-color")) > 0.5) {
            $(".DynamicNav_currentpage_link").addClass("Darken");
            $(".DynamicNav ul li ul li a").addClass("Darken");
        } else {
            $(".DynamicNav_currentpage_link").removeClass("Darken");
            $(".DynamicNav ul li ul li a").removeClass("Darken");
        }
    }
}, 1);
function colorValues(color) {
    color = color.replace("rgb(", "");
    color = color.replace(")", "");
    colors = color.split(", ");
    var R = 0.2126 * parseInt(colors[0]) / 255;
    var G = 0.7152 * parseInt(colors[1]) / 255;
    var B = 0.0722 * parseInt(colors[2]) / 255;
    return R + G + B;
}

