    function slm(){
    	$.schoolListMinibase({
            'miniBaseDomainID': 4,
			'miniBaseModuleInstanceID': 2747,
            'cache': true,
            'schoolListReady': function(schoolList) {
            	//will hold each groupings LI HTML
				var schools = {
                	elemSchools: [],
                    middleSchools: [],
                    highSchools: [],
                    altSchools: []
                };
                //holds the HTML to be added to the body and starts off with a main column
                var schoolListHtml = '

';


                //split up our schools accordingly
                for(var i in schoolList) {
                	if( schoolList[i].schoolVisible.toLowerCase() == 'true' ){ //if the school is set to show then push it up to it's appropriate bucket
                        switch(schoolList[i].schoolType.toLowerCase()){
                            case "elementary":
                                schools.elemSchools.push('
' + schoolList[i].schoolName + '');
                                break;
                            case "middle":
                            	schools.middleSchools.push('
' + schoolList[i].schoolName + '');
                                break;
                            case "high":
                            	schools.highSchools.push('
' + schoolList[i].schoolName + '');
                                break;
                            case "additional programs": //changed from other schools by Lyndsay - Case 00520520 - 4.25.17
                            	schools.altSchools.push('
' + schoolList[i].schoolName + '');
                                break;
                            default:
                            	//pass
                                break;
                        }
                    }
                }

               	var schoolsCount = 0; //will use this to determine when a new main column is needed

                //go over every school HTML bucket and build out the columns

				/* Column DOM Logic - this is used for each school bucket.
                	[h1 - school header]
                    [div - list column]
                    	[ul - schools; can only have 11 items after that a new list column is started]
                */
                for(var list in schools){
                	if( schools[list].length > 0 ){
                    	//we only put two schools in a column, and we only have 4 groups
                        schoolsCount++;
                        if( schoolsCount == 3 ){
                        	schoolListHtml += '
'
                        }


                        var listType = "";
                        var headerHtml = "";
                        //build out the header
                        switch(list){
                        	case "elemSchools":
                            	headerHtml = '
Elementary';
                                listType = "elementary-schools";
                            	break;
                            case "middleSchools":
                            	headerHtml = '
Middle';
                                listType = "middle-schools";
                            	break;
                            case "highSchools":
                            	headerHtml = '
High';
                                listType = "high-schools";
                            	break;
                            case "altSchools":
                            	headerHtml = '
Additional Programs'; //changed to 'Additional Programs' by Lyndsay - Case 523134 - 4.25.17
                                listType = "alt-schools";
                            	break;
                            default:
                            	//pass
                                break;
                        }

                        var loopCount = 0; //will make sure we only have 11 items per list column
                        schoolListHtml += '
' + headerHtml + '
';
                    	for(var html in schools[list]){
                        	loopCount++;
                            schoolListHtml += schools[list][html]; //add the LI HTML to the markup
                            if(loopCount == 11){
                            	schoolListHtml += ''; //close out the last list and column
                            	schoolListHtml += '
'; //open a new column and list
                            }
                        }
                        schoolListHtml += '' //close out this schools list and container so a new one can begin

                    }
                }
                schoolListHtml += ''; //close out the main container

                $('#school-list').append(schoolListHtml); //add the HTML markup to the page container

                //if we had a middle school move it into the elementary schools last column
                $('#school-list .elementary-schools > div').last()
                	.append($('#middle-school-header'))
                    .append($('.middle-schools'));

                //hook in our a11y event handlers
                $('#school-list a').first().keydown(function(e){ //if we are on the first link and we are trying to go backwards jump back to the trigger to close the menu
                    if(e.which == 9 && e.shiftKey){
                        e.preventDefault();
                        $('#gb-school-list').focus();
                    }
                });
                $('#school-list a').last().keydown(function(e){ //if we are on the last link and trying to move forward then jump to the search input
                    if(e.which == 9 && !e.shiftKey){
                        e.preventDefault();
                        $('#school-list a').first().focus();
                    }
                });

                rsMenu(schoolList); //pass our list to the mobile menu
            }
        });
    }

    function rsMenu(schoolList){

        /* added by Matt B - Custom Hourly 08-17-17 */

        var headerLinks = [
            ["https://staffdir.bismarckschools.org/", "Staff Directory"]
        ];
        var extraLinks = "";
        var extraLinkLabels = "";

        $.each(headerLinks, function(urlIndex, url){
        	extraLinks += ( url[0] != "" ) ? "1," + url[1] + "," + url[0] + ",_blank;" : "";
        });

        if( extraLinks != "" ) extraLinkLabels += "Header Links";

    	// RESPONSIVE MAIN MENU
		$("body").csRsMenu({
		    "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
			"primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			"secondaryColor"        : "#222A30", // DEFAULT - #222A30
			"accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			"menuButtonParent"      : "#gb-channel-list",
			"showMenuBtnText"       : "yes",
			"menuBtnText"           : "MENU",
			"showSchools"           : "no",
			"showTranslate"         : "yes",
			"translateId"           : "none",
			"showAccount"           : "yes",
			"extraMenuOptions"      : extraLinkLabels, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"        : extraLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"                : "4"
		});
    }

	function pgScreenSize(){
		var getWidth = $(window).width();

        multimediaRotator();

		if (getWidth < 1024) { //Mobile BPs
			appView();
            if( !$('#social-media-links').hasClass('mobile-move') ){
            	$('#social-media-links').addClass('mobile-move');
                $('#footer-info').after($('#social-media-links'));
            }

            if( !$('#footer-nav-links').hasClass('mobile-move') ){
            	$('#footer-nav-links').addClass('mobile-move');
                $('#extra-footer-links').before($('#footer-nav-links'));
            }

            if( $('.sp.column.one').length > 0 ){
            	$('.sp.column.one .ui-widget-header').off('click').click(function(e){
                	$('.sp.column.one .app.navigation').toggleClass('active');
                	$('.sp.column.one .ui-widget-detail').stop(true).slideToggle();
                });
            }

		}else{
        	if( $('#social-media-links').hasClass('mobile-move') ){
            	$('#social-media-links').removeClass('mobile-move');
                $('#extra-footer-links').before($('#social-media-links'));
            }

            if( $('#footer-nav-links').hasClass('mobile-move') ){
            	$('#footer-nav-links').removeClass('mobile-move');
                $('#footer-info').after($('#footer-nav-links'));
            }
            if( $('.sp.column.one').length > 0 ){
            	$('.sp.column.one .ui-widget-detail').attr('style', '');
            	$('.sp.column.one .ui-widget-header').off('click');
                $('.sp.column.one .app.navigation').removeClass('active');
            }
        }
	}

